INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '10.1.2'), 1, 'DGAC — LÍDER DA AÇÃO',
    'DGAC', 'LÍDER DA AÇÃO', 'A DGAC é o órgão responsável pela conscientização comunitária e pela promoção da percepção de risco. Nesta ação, lidera o ciclo completo: cria, analisa, sistematiza e modela os projetos-prateleira de proteção comunitária. Define o escopo de cada projeto, elabora materiais de apoio, roteiros de implementação e critérios de adaptação local. A DGAC detém o conhecimento sobre as dinâmicas comunitárias e sobre os formatos que geram engajamento nas comunidades. Quando necessário, solicita ao ICTDEC (com CEPEDEC) suporte científico e metodológico específico.',
    ARRAY['Liderar o ciclo completo de produção do portfólio: criação, análise, sistematização e modelagem dos projetos-prateleira', 'Definir escopo, materiais de apoio, roteiros de implementação e critérios de adaptação local', 'Analisar e sistematizar as práticas coletadas pelas REDECs, identificando elementos replicáveis', 'Validar a aplicabilidade comunitária dos projetos antes da disponibilização no portfólio', 'Solicitar ao ICTDEC suporte científico quando a análise exigir rigor metodológico específico'], ARRAY['Não capacitar formalmente agentes municipais (competência da ESDEC)', 'Não coletar práticas nos municípios (competência das REDECs via DREDEC)'], ARRAY['Práticas coletadas pelas REDECs como matéria-prima', 'Validação estratégica da SUOP', 'Suporte científico do ICTDEC/CEPEDEC quando solicitado', 'Capacitação da ESDEC para viabilizar a implementação municipal']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '10.1.2') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'DGAC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '10.1.2'), 2, 'ICTDEC (com CEPEDEC) — SUPORTE CIENTÍFICO SOB DEMANDA',
    'ICTDEC (com CEPEDEC)', 'SUPORTE CIENTÍFICO SOB DEMANDA', 'O ICTDEC, por meio do CEPEDEC, é o produtor de conhecimento científico e metodologias da Secretaria. Nesta ação, atua sob demanda da DGAC: quando a análise ou sistematização das práticas comunitárias exigir rigor científico específico — como aplicação de metodologias estatísticas robustas, validação técnico-científica de modelos ou produção de referências acadêmicas —, o ICTDEC é acionado. A liderança da criação, análise e sistematização permanece com a DGAC.',
    ARRAY['Fornecer suporte científico e metodológico à DGAC quando solicitado', 'Aplicar metodologias estatísticas robustas quando a análise das práticas exigir', 'Validar tecnicamente modelos de projeto que demandem fundamentação científica específica', 'Apoiar a ESDEC com conteúdo científico para capacitação, quando solicitado'], ARRAY['Não liderar a criação, análise ou sistematização das práticas (competência da DGAC)', 'Não modelar projetos-prateleira (competência da DGAC)', 'Não difundir o portfólio aos municípios (competência das REDECs via DREDEC)', 'Não conduzir capacitações (competência da ESDEC)'], ARRAY['Demandas específicas da DGAC sobre temas que exijam rigor científico', 'Acesso às práticas já coletadas e pré-analisadas pela DGAC', 'Parcerias com IES e ICTs (AE 6.1.3) como fonte complementar de conhecimento']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '10.1.2') AND ordem = 2), (SELECT id FROM setores WHERE codigo = 'ICTDEC'), 'principal');
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '10.1.2') AND ordem = 2), (SELECT id FROM setores WHERE codigo = 'CEPEDEC'), 'participante');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '10.1.2'), 3, 'ESDEC — CAPACITADORA',
    'ESDEC', 'CAPACITADORA', 'A ESDEC capacita agentes do SIEPDEC-RJ. Nesta ação, forma agentes municipais para a implementação dos projetos do portfólio: como aplicar as metodologias, adaptar os projetos à realidade local, engajar comunidades e monitorar resultados. Os conteúdos são fornecidos pela DGAC (modelagem) e pelo ICTDEC (fundamentação metodológica).',
    ARRAY['Estruturar e executar capacitações para agentes municipais na implementação dos projetos do portfólio', 'Incluir conteúdos de aplicação de metodologias, adaptação local, engajamento comunitário e monitoramento', 'Avaliar o aprendizado e retroalimentar o modelo de capacitação'], ARRAY['Não modelar projetos (competência da DGAC)', 'Não sistematizar práticas (competência do ICTDEC/CEPEDEC)', 'Não articular adesão municipal (competência das REDECs via DREDEC)'], ARRAY['Conteúdos fornecidos pela DGAC e pelo ICTDEC/CEPEDEC', 'Demanda dimensionada pela SUOP', 'Apoio logístico das REDECs para capacitação no território']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '10.1.2') AND ordem = 3), (SELECT id FROM setores WHERE codigo = 'ESDEC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '10.1.2'), 4, 'REDECs (via DREDEC) — COLETA DE PRÁTICAS E DIFUSÃO AOS MUNICÍPIOS',
    'REDECs (via DREDEC)', 'COLETA DE PRÁTICAS E DIFUSÃO AOS MUNICÍPIOS', 'As REDECs são o elo entre o Estado e os municípios. Nesta ação, atuam nas duas pontas do ciclo: na entrada, colhem junto aos municípios e COMDECs as melhores práticas de projetos comunitários em PDC já existentes, alimentando o ICTDEC com matéria-prima para sistematização; na saída, disponibilizam o portfólio aos municípios, articulam a adesão e acompanham a implementação.',
    ARRAY['Colher junto aos municípios e COMDECs as melhores práticas de projetos comunitários em PDC', 'Disponibilizar o portfólio de projetos aos municípios de sua região', 'Articular a adesão municipal aos projetos do portfólio', 'Acompanhar e reportar a implementação dos projetos nos municípios'], ARRAY['Não modelar projetos (competência da DGAC)', 'Não sistematizar metodologicamente (competência do ICTDEC/CEPEDEC)', 'Não capacitar formalmente agentes municipais (competência da ESDEC)', 'Não formalizar convênios sem aprovação da SUBSEGAD e validação da ASSEJUR'], ARRAY['Orientações da DGAC sobre o que coletar e como difundir', 'Portfólio finalizado e validado para disponibilização', 'Priorização de municípios definida pela SUOP']);
