INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '10.1.2'), 5, 'SUOP (sob aval da SUBSEGOP) — VALIDAÇÃO E COORDENAÇÃO',
    'SUOP (sob aval da SUBSEGOP)', 'VALIDAÇÃO E COORDENAÇÃO', 'A SUOP comanda os órgãos setoriais. Nesta ação, valida o portfólio quanto ao alinhamento ao Plano Estratégico, coordena a integração entre os órgãos envolvidos (DGAC, ICTDEC, ESDEC, REDECs) e define critérios de priorização de municípios para difusão e capacitação.',
    ARRAY['Validar o portfólio quanto ao alinhamento ao Plano Estratégico e às prioridades da SEDEC', 'Coordenar a integração entre DGAC, ICTDEC, ESDEC e REDECs na produção e difusão', 'Definir critérios de priorização de municípios para adesão e capacitação', 'Monitorar o ciclo de produção e os resultados da adesão municipal'], ARRAY['Não produzir conteúdo (competência da DGAC e do ICTDEC)', 'Não executar na ponta (competência das REDECs)', 'Não conduzir capacitações (competência da ESDEC)'], ARRAY['Colaboração dos órgãos envolvidos na execução de suas partes', 'Informações das REDECs sobre necessidades e adesão municipal']);
