INSERT INTO panoramico_setores (panoramico_linha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM panoramico_linhas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.1') AND ordem = 4), (SELECT id FROM setores WHERE codigo = 'ÓRGÃOS_SUBORDINADOS'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '2.1.1'), 1, 'Alta Gestão (SUBSEGOP / Secretário) — LÍDER INSTITUCIONAL',
    'Alta Gestão (SUBSEGOP / Secretário)', 'LÍDER INSTITUCIONAL', 'A institucionalização da gestão estratégica requer envolvimento direto da Alta Gestão para que as rotinas de monitoramento sejam reconhecidas como compromisso institucional, e não como iniciativa técnica isolada. A participação da liderança confere autoridade ao processo e assegura que os resultados do monitoramento se traduzam em decisões concretas de gestão.',
    ARRAY['Chancelar institucionalmente a sistemática de monitoramento estratégico', 'Presidir ou designar representante para as reuniões de análise crítica de desempenho (RACs)', 'Validar o conjunto de indicadores estratégicos propostos pelo ICTDEC', 'Tomar decisões de correção de rota com base nos relatórios de monitoramento', 'Assegurar que os órgãos subordinados cumpram as rotinas de reporte', 'Comunicar os resultados do monitoramento para fins de transparência e prestação de contas'], ARRAY['Não elaborar a metodologia de monitoramento (competência técnica do ICTDEC/DAEAD)', 'Não consolidar dados ou produzir relatórios técnicos (competência do ICTDEC/DAEAD)', 'Não utilizar o monitoramento como instrumento punitivo (o sistema deve orientar a melhoria contínua, não a sanção)'], ARRAY['Metodologia de monitoramento estruturada e operacional (ICTDEC/DAEAD)', 'Indicadores validados e fontes de dados acessíveis', 'Comprometimento dos órgãos subordinados com o reporte periódico', 'Infraestrutura mínima de TI para consolidação de dados']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.1') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'SUBSEGOP'), 'principal');
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.1') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'SECRETARIO'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '2.1.1'), 2, 'ICTDEC (DAEAD) — EXECUTOR CENTRAL TÉCNICO-METODOLÓGICO',
    'ICTDEC (DAEAD)', 'EXECUTOR CENTRAL TÉCNICO-METODOLÓGICO', 'O Plano Estratégico 2024–2035 atribui à Divisão de Análise Estratégica e Apoio à Decisão (DAEAD) do ICTDEC a responsabilidade pelo monitoramento global do Plano. A DAEAD detém a competência metodológica para definir indicadores, consolidar dados e produzir as análises que subsidiam a tomada de decisão da Alta Gestão.
Além do monitoramento, compete ao ICTDEC a difusão da cultura de gestão estratégica na instituição, em consonância com o Programa SEDEC 360 e com a Metodologia de Planos Setoriais, que dependem diretamente desta ação para sua base metodológica.',
    ARRAY['Estabelecer a metodologia e o macroprocesso do planejamento estratégico da Secretaria, incluindo ciclos de revisão, fluxos de reporte e calendário de análises críticas', 'Definir, em articulação com os órgãos subordinados, o conjunto de indicadores de desempenho (eficácia, eficiência e efetividade) e suas fontes de dados', 'Criar e manter a estrutura para condução da estratégia: implementação, acompanhamento, avaliação, monitoramento e correção', 'Consolidar os dados reportados pelos órgãos subordinados e produzir relatórios de análise crítica para a Alta Gestão', 'Conduzir tecnicamente as reuniões periódicas de monitoramento com os gestores dos órgãos subordinados', 'Promover ações de difusão e capacitação em gestão estratégica junto aos gestores da Secretaria', 'Coordenar a elaboração e revisão dos Planos Setoriais conforme metodologia vigente', 'Produzir notas técnicas e briefings executivos a partir da integração dos dados de monitoramento'], ARRAY['Não decidir prioridades políticas ou estratégicas (competência da Alta Gestão)', 'Não executar as ações estratégicas dos demais setores (apenas monitora e assessora)', 'Não gerir o portfólio de projetos operacionais (competência do Gabinete de Gestão de Projetos, no âmbito da AE 2.1.2)', 'Não substituir os órgãos subordinados na coleta de seus próprios dados e indicadores'], ARRAY['Chancela da Alta Gestão para conferir autoridade institucional ao sistema de monitoramento', 'Articulação operacional da SUOP para garantir adesão dos órgãos subordinados', 'Comprometimento das chefias setoriais com o fornecimento regular de dados e a participação nas análises críticas', 'Infraestrutura mínima de TI para consolidação de dados (considerando as condições atuais, podem ser soluções gratuitas adaptadas)', 'Tempo protegido da equipe DAEAD para atividades de monitoramento', 'Gabinete de Gestão de Projetos funcional (interface com o monitoramento estratégico para cruzamento entre planejamento e execução de projetos — AE 2.1.2)']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.1') AND ordem = 2), (SELECT id FROM setores WHERE codigo = 'ICTDEC'), 'principal');
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.1') AND ordem = 2), (SELECT id FROM setores WHERE codigo = 'DAEAD'), 'participante');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '2.1.1'), 3, 'SUOP (sob aval da SUBSEGOP) — ARTICULADOR OPERACIONAL',
    'SUOP (sob aval da SUBSEGOP)', 'ARTICULADOR OPERACIONAL', 'A SUOP constitui o elo entre o núcleo técnico-estratégico (ICTDEC) e os órgãos que executam na ponta. O ICTDEC não possui autoridade hierárquica direta sobre os demais órgãos subordinados, o que torna a intermediação da SUOP indispensável para assegurar a adesão institucional às rotinas de monitoramento.',
    ARRAY['Garantir a aderência dos órgãos subordinados às rotinas de monitoramento definidas pelo ICTDEC', 'Intermediar a relação entre o núcleo de planejamento (ICTDEC/DAEAD) e os órgãos executores', 'Destravar gargalos de participação (priorização de agenda, liberação de pessoal, cobrança de reportes)', 'Contribuir com a validação operacional dos indicadores propostos', 'Encaminhar à Alta Gestão as questões que exijam decisão política identificadas nos ciclos de monitoramento'], ARRAY['Não elaborar a metodologia de monitoramento ou definir indicadores (competência do ICTDEC)', 'Não produzir relatórios de análise crítica (competência do ICTDEC)', 'Não alterar prioridades estratégicas sem validação da Alta Gestão', 'Não centralizar o fluxo de informações de monitoramento (órgãos reportam ao ICTDEC; a SUOP assegura que o façam)'], ARRAY['Metodologia clara e calendário de monitoramento definido pelo ICTDEC', 'Compromisso da Alta Gestão com o processo', 'Colaboração dos órgãos subordinados']);
