VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.2') AND ordem = 3), (SELECT id FROM setores WHERE codigo = 'SUBSEGOP'), 'aval');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '2.1.2'), 4, 'SUBSEGAD — APOIO FINANCEIRO E ADMINISTRATIVO',
    'SUBSEGAD', 'APOIO FINANCEIRO E ADMINISTRATIVO', 'A gestão de projetos requer informações financeiras atualizadas e conformidade contábil, especialmente para projetos financiados por fontes externas. A SUBSEGAD fornece os dados de execução orçamentária que alimentam o monitoramento físico-financeiro realizado pelo Gabinete.',
    ARRAY['Fornecer informações de execução orçamentária e financeira dos projetos ao Gabinete de Gestão de Projetos', 'Garantir conformidade contábil e jurídica dos instrumentos vinculados aos projetos', 'Apoiar a prestação de contas de projetos financiados por fontes externas', 'Sinalizar tempestivamente restrições orçamentárias que impactem o portfólio'], ARRAY['Não gerir projetos (competência do Gabinete)', 'Não definir metodologia de gestão de projetos (competência do ICTDEC)', 'Não priorizar projetos (competência da SUOP/Alta Gestão)'], ARRAY['Portfólio de projetos estruturado e atualizado (Gabinete)', 'Critérios claros de classificação orçamentária dos projetos', 'Parecer da ASSEJUR quando necessário (instrumentos de repasse, convênios)']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.2') AND ordem = 4), (SELECT id FROM setores WHERE codigo = 'SUBSEGAD'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '2.1.2'), 5, 'Órgãos Subordinados (Direções, Coordenações, REDECs e demais setores) — USUÁRIOS DA METODOLOGIA E EXECUTORES DE PROJETOS',
    'Órgãos Subordinados (Direções, Coordenações, REDECs e demais setores)', 'USUÁRIOS DA METODOLOGIA E EXECUTORES DE PROJETOS', 'Os órgãos subordinados são os responsáveis pela execução dos projetos e, portanto, os usuários diretos da metodologia institucionalizada. Sua adesão ao método padronizado é o que converte a metodologia em prática efetiva.',
    ARRAY['Aplicar a metodologia de gestão de projetos definida institucionalmente em suas iniciativas', 'Reportar ao Gabinete de Gestão de Projetos o status de execução de seus projetos, conforme fluxos definidos', 'Participar das capacitações em gestão de projetos promovidas pelo ICTDEC', 'Utilizar as ferramentas tecnológicas padronizadas para gerenciamento de suas iniciativas', 'Fornecer informações tempestivas sobre desvios, riscos e necessidades de reprogramação'], ARRAY['Não criar metodologias paralelas de gestão de projetos (a padronização é institucional)', 'Não alterar prioridades do portfólio sem validação da SUOP', 'Não tratar o reporte ao Gabinete como atividade acessória (integra a gestão corrente)'], ARRAY['Metodologia simples, objetiva e compatível com a capacidade operacional dos setores', 'Ferramentas tecnológicas acessíveis e de fácil utilização', 'Capacitação adequada promovida pelo ICTDEC', 'Retorno efetivo do monitoramento (o reporte deve gerar suporte, não apenas registro)']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
