INSERT INTO panoramico_setores (panoramico_linha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM panoramico_linhas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.5') AND ordem = 3), (SELECT id FROM setores WHERE codigo = 'ÓRGÃOS_SUBORDINADOS'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '2.1.5'), 1, 'SUOP (sob aval da SUBSEGOP) — LÍDER COORDENADOR',
    'SUOP (sob aval da SUBSEGOP)', 'LÍDER COORDENADOR', 'A revisão periódica de normas internas exige coordenação entre múltiplos setores, pois as normas regulam atividades transversais. A SUOP, como órgão de integração operacional, é a instância com capacidade de mobilizar os órgãos, constituir e coordenar os Grupos de Trabalho e assegurar que o ciclo de revisão se cumpra dentro dos prazos. Sem essa coordenação centralizada, as revisões tendem a ocorrer de forma reativa (apenas quando surgem problemas) e fragmentada.',
    ARRAY['Coordenar o ciclo de revisão normativa, utilizando a metodologia e o calendário definidos pelo ICTDEC', 'Constituir e coordenar os Grupos de Trabalho (GTs) para aprimoramento das normas internas, com participação dos órgãos afetados', 'Mobilizar os órgãos subordinados para o fornecimento de insumos e validação técnica das minutas', 'Incluir no Calendário Anual de Atividades da SEDEC (AE 2.1.3) o período dedicado à revisão das normas internas', 'Encaminhar as minutas revisadas para parecer da ASSEJUR e posterior aprovação da Alta Gestão', 'Manter registro formal das revisões realizadas, incluindo justificativa e data de vigência'], ARRAY['Não definir a metodologia de revisão normativa (competência do ICTDEC)', 'Não emitir parecer jurídico sobre as normas revisadas (competência da ASSEJUR)', 'Não aprovar normas sem chancela da Alta Gestão', 'Não tratar normas externas (legislação federal e estadual) — escopo da AE 7.1.1'], ARRAY['Metodologia, ciclos e templates de revisão definidos pelo ICTDEC', 'Parecer jurídico da ASSEJUR para validação das minutas revisadas', 'Comprometimento dos órgãos subordinados com a participação nos GTs e fornecimento de insumos', 'Chancela da Alta Gestão para publicação das normas revisadas', 'Coletânea atualizada do arcabouço normativo vigente (produto inicial do ICTDEC)']);
