INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '2.1.6'), 1, 'SUOP (sob aval da SUBSEGOP) — LÍDER COORDENADOR E SUPERVISOR',
    'SUOP (sob aval da SUBSEGOP)', 'LÍDER COORDENADOR E SUPERVISOR', 'A gestão de processos exige coordenação transversal, pois os processos finalísticos frequentemente cruzam fronteiras entre órgãos. A SUOP, como instância de integração operacional, é quem articula a participação dos setores na fase de implantação e, após esta, supervisiona a manutenção descentralizada pelos órgãos. Cabe também à SUOP validar a priorização dos processos a serem mapeados, assegurando alinhamento à estratégia.',
    ARRAY['Coordenar a implantação da gestão de processos, assegurando a participação dos órgãos nas oficinas de mapeamento conduzidas pelo ICTDEC', 'Validar a priorização dos processos a serem mapeados, em articulação com a Alta Gestão', 'Supervisionar a manutenção descentralizada dos processos mapeados pelos órgãos subordinados', 'Destravar gargalos de participação (liberação de pessoal, agenda, engajamento dos setores)', 'Integrar o calendário de oficinas e revisões de processos ao planejamento operacional da Superintendência', 'Articular com o ICTDEC a resolução de interfaces críticas entre processos de órgãos distintos', 'Manter o repositório institucional de processos mapeados, validando e incorporando as atualizações encaminhadas pelos órgãos subordinados'], ARRAY['Não definir a metodologia de gestão de processos (competência do ICTDEC)', 'Não conduzir as oficinas de mapeamento (competência do ICTDEC)', 'Não mapear processos diretamente (competência dos órgãos com apoio do ICTDEC)'], ARRAY['Metodologia de gestão de processos e estrutura do repositório definidas pelo ICTDEC', 'Comprometimento dos órgãos subordinados com a participação nas oficinas e o encaminhamento de atualizações', 'Ferramenta ou mecanismo para manutenção do repositório (definido pelo ICTDEC, operado pela SUOP)', 'Tempo protegido das equipes dos órgãos para participação nas oficinas']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.6') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'SUOP'), 'principal');
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.6') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'SUBSEGOP'), 'aval');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '2.1.6'), 2, 'ICTDEC — COEXECUTOR METODOLÓGICO',
    'ICTDEC', 'COEXECUTOR METODOLÓGICO', 'O ICTDEC é responsável pela formulação da metodologia organizacional de gestão de processos e pela condução dos mapeamentos iniciais. Sua contribuição divide-se em duas fases: na implantação, define a metodologia, conduz as oficinas de mapeamento com os órgãos e capacita as equipes; na manutenção, atua como consultor metodológico, apoiando eventuais atualizações da metodologia ou mapeamentos de maior complexidade, sem assumir a responsabilidade recorrente de manter os processos de cada setor.',
    ARRAY['Estabelecer a metodologia organizacional de gestão de processos, incluindo padrões de notação, níveis de detalhamento e critérios de priorização', 'Conduzir as oficinas de mapeamento de processos junto aos órgãos subordinados, com foco prioritário nos processos finalísticos', 'Capacitar os órgãos subordinados para que mantenham seus processos mapeados de forma autônoma', 'Apoiar a seleção e configuração de ferramenta ou mecanismo para registro e gerenciamento dos processos', 'Difundir os fundamentos da gestão de processos na instituição', 'Atuar como consultor metodológico após a implantação, apoiando atualizações de maior complexidade ou revisões da metodologia', 'Assegurar a coerência entre os processos mapeados e as normas internas (AE 2.1.5) e as competências requeridas por função (AE 2.1.7)'], ARRAY['Não manter os processos de outros órgãos (responsabilidade do setor que executa o processo)', 'Não supervisionar o ciclo recorrente de manutenção (competência da SUOP)', 'Não priorizar quais processos serão mapeados (validação da SUOP/Alta Gestão)', 'Não mapear todos os processos da Secretaria (a implantação é gradual, priorizando processos finalísticos críticos)'], ARRAY['Priorização dos processos a serem mapeados (SUOP/Alta Gestão)', 'Participação efetiva dos órgãos subordinados nas oficinas', 'Tempo protegido da equipe do ICTDEC para condução das oficinas', 'Acesso às normas internas vigentes (AE 2.1.5) para cruzamento com os processos mapeados']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.6') AND ordem = 2), (SELECT id FROM setores WHERE codigo = 'ICTDEC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '2.1.6'), 3, 'Órgãos Subordinados (Direções, Coordenações, REDECs e demais setores) — PARTICIPANTES, EXECUTORES E PROPONENTES DE ATUALIZAÇÃO',
    'Órgãos Subordinados (Direções, Coordenações, REDECs e demais setores)', 'PARTICIPANTES, EXECUTORES E PROPONENTES DE ATUALIZAÇÃO', 'Os órgãos subordinados têm três papéis distintos nesta ação. Na implantação, participam das oficinas de mapeamento conduzidas pelo ICTDEC, contribuindo com o conhecimento operacional sobre como os processos realmente funcionam. Na operação, executam seus processos conforme os fluxos padronizados. Na manutenção, identificam necessidades de atualização e encaminham as propostas à SUOP para validação e incorporação ao repositório. Têm acesso de leitura e download das fichas de processos no repositório institucional.',
    ARRAY['Participar das oficinas de mapeamento conduzidas pelo ICTDEC, contribuindo com conhecimento operacional', 'Executar os processos conforme os fluxos padronizados e validados', 'Identificar necessidades de atualização nos processos e encaminhar propostas à SUOP para validação e incorporação ao repositório', 'Reportar gargalos, interfaces críticas e oportunidades de melhoria nos processos', 'Participar das capacitações em gestão de processos promovidas pelo ICTDEC'], ARRAY['Não criar metodologias próprias de mapeamento (a padronização é institucional)', 'Não alterar processos padronizados sem validação da SUOP', 'Não sobrescrever fichas diretamente no repositório (toda atualização passa pela SUOP para validação)'], ARRAY['Metodologia clara, objetiva e compatível com a capacidade operacional dos setores', 'Capacitação adequada para identificação de necessidades de atualização (ICTDEC)', 'Acesso de leitura e download das fichas de processos no repositório institucional mantido pela SUOP', 'Consultoria metodológica do ICTDEC para situações de maior complexidade', 'Canal claro para encaminhamento de propostas de atualização à SUOP']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.6') AND ordem = 3), (SELECT id FROM setores WHERE codigo = 'ORGAOS_SUBORDINADOS'), 'principal');
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.6') AND ordem = 3), (SELECT id FROM setores WHERE codigo = 'REDECS'), 'participante');
INSERT INTO fundamentacoes (acao_estrategica_id, introducao, conclusao)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '2.1.6'), 'A ausência de gestão de processos institucionalizada gera três efeitos operacionais:', 'Esta ação enfrenta esses efeitos ao implantar uma metodologia padronizada de mapeamento (continuidade e impessoalidade), uma visão integrada pela Cadeia de Valor (conexão entre etapas) e um modelo de manutenção com repositório centralizado e atualização propositiva dos órgãos (escalabilidade com governança).');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.6')), 1, '1. Dependência de conhecimento individual: Quando os processos não estão mapeados e padronizados, a execução depende de quem sabe fazer. A rotatividade de pessoal, natural em órgãos públicos, gera perda de capacidade operacional a cada movimentação, pois o conhecimento sai com a pessoa.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.6')), 2, '2. Desconexão entre setores: Processos finalísticos frequentemente envolvem múltiplos órgãos. Sem a visão integrada da cadeia de valor, cada setor otimiza apenas a sua etapa, gerando descompassos nas interfaces: um setor entrega fora do formato que o próximo precisa, prazos não se conectam, retrabalho se acumula.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.6')), 3, '3. Impossibilidade de melhoria sistemática: Sem processos mapeados, não há base para identificar gargalos, medir eficiência ou propor otimizações. As melhorias acontecem de forma intuitiva e localizada, sem escala nem rastreabilidade.');

-- AE 2.1.7
INSERT INTO acoes_estrategicas (numero, nome, eixo_prioritario_id, objetivo_estrategico_id, estrategia_id,
    acoes_conectadas, descricao_oficial_completa, descricao_o_que, descricao_para_que, ancoragem,
    nota_arranjo_institucional, versao, data_elaboracao, elaboracao, proxima_fase)
VALUES ('2.1.7', 'Implantar e implementar a gestão por competências', (SELECT id FROM eixos_prioritarios WHERE codigo = 'EIXO_1'), (SELECT id FROM objetivos_estrategicos WHERE codigo = 'OE2'), (SELECT id FROM estrategias WHERE codigo = 'E2.1'),
    'AE 2.1.4 (Cultura organizacional) | AE 2.1.5 (Revisão de normas/regimentos) | AE 2.1.6 (Gestão de processos) | AE 3.1.1 (Capacitação do efetivo)',
    'Estabelecimento de um modelo de gestão que integre o mapeamento, a avaliação e o desenvolvimento contínuo de conhecimentos, habilidades e atitudes (O QUÊ), visando alinhar o capital humano aos objetivos estratégicos da organização e potencializar a performance individual e coletiva (PARA QUÊ).',
    'Estabelecimento de um modelo de gestão que integre o mapeamento, a avaliação e o desenvolvimento contínuo de conhecimentos, habilidades e atitudes.',
    'Alinhar o capital humano aos objetivos estratégicos da organização e potencializar a performance individual e coletiva.',
    'A gestão por competências é o instrumento que conecta as funções da estrutura organizacional às capacidades necessárias para executá-las. O Plano Estratégico 2024–2035 posiciona esta ação com três iniciativas sequenciais: mapear as funções da estrutura, mapear as competências necessárias e desejáveis para cada função, e mapear as competências da força de trabalho. A sequência é deliberada: não é possível definir competências sem antes saber quais funções existem e quais processos cada uma executa. Por isso, esta AE tem dependência direta da gestão de processos (AE 2.1.6): os processos mapeados são o insumo primário para identificar os conhecimentos, habilidades e atitudes que cada função exige. Há também interdependência com a revisão de normas internas (AE 2.1.5): regimentos internos que reflitam com precisão as funções e atribuições de cada setor são base formal para o mapeamento de competências. O Objetivo Estratégico 3 (Desenvolver o efetivo) reforça essa conexão ao prever trilhas de aprendizagem, capacitações e gestão de pessoas por competências com foco estratégico.',
    NULL,
    '1.0',
    '2026-02-14',
    'ICTDEC/DAEAD',
    'Fase 2 — Desdobramento Orientado (definição de entregas por setor)');
INSERT INTO destaques_estrategicos (acao_estrategica_id, titulo, header_contexto)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '2.1.7'), 'A Cadeia Lógica Função → Processo → Competência', 'CONTEXTO — Pré-requisitos e Sequência');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.7')), 1, 'label_conteudo', 'Pré-requisito', 'A gestão por competências só produz resultado quando apoiada em dados concretos sobre o que cada função efetivamente faz. Sem o mapeamento de processos (AE 2.1.6), a definição de competências tende a ser genérica, baseada em descrições formais de cargo que nem sempre correspondem à realidade operacional. A AE 2.1.6 é, portanto, insumo direto desta ação.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.7')), 2, 'label_conteudo', 'Sequência lógica', '1. Mapear funções da estrutura da SEDEC (o que existe) → 2. Mapear competências necessárias e desejáveis para cada função (o que se precisa) → 3. Mapear competências da força de trabalho (o que se tem) → 4. Identificar lacunas e orientar desenvolvimento (o que falta).');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.7')), 3, 'label_conteudo', 'Saída esperada', 'A lacuna entre competências requeridas e existentes alimenta diretamente a grade de capacitações da ESDEC (AE 3.1.1), as trilhas de aprendizagem e a política de alocação de pessoal. O modelo também subsidia decisões de movimentação, seleção e reconhecimento.');
