INSERT INTO panoramico_setores (panoramico_linha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM panoramico_linhas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.7') AND ordem = 4), (SELECT id FROM setores WHERE codigo = 'ÓRGÃOS_SUBORDINADOS'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '2.1.7'), 1, 'SUOP (sob aval da SUBSEGOP) — LÍDER COORDENADOR',
    'SUOP (sob aval da SUBSEGOP)', 'LÍDER COORDENADOR', 'A gestão por competências impacta diretamente a rotina de todos os órgãos da Secretaria e toca em temas sensíveis (alocação, avaliação, desenvolvimento). A SUOP, como órgão de integração operacional, coordena a implantação junto aos setores e assegura que o modelo seja aplicado de forma uniforme. A SUBSEGOP patrocina institucionalmente a ação, conferindo a legitimidade necessária para que os mapeamentos e diagnósticos tenham adesão.',
    ARRAY['Coordenar a implantação do modelo de gestão por competências, assegurando a participação dos órgãos', 'Mobilizar os órgãos subordinados para o fornecimento de informações sobre funções e competências', 'Validar os mapeamentos de funções e competências elaborados pelo ICTDEC, em articulação com os órgãos afetados', 'Supervisionar a aplicação do modelo pelos órgãos subordinados após a implantação', 'Articular com a ESDEC para que as lacunas de competência identificadas se traduzam em ações de desenvolvimento', 'Reportar à SUBSEGOP os resultados dos diagnósticos para subsidiar decisões de gestão de pessoas'], ARRAY['Não definir a metodologia de gestão por competências (competência do ICTDEC)', 'Não conduzir tecnicamente os mapeamentos (competência do ICTDEC)', 'Não executar ações de capacitação (competência da ESDEC)', 'Não decidir unilateralmente movimentação ou alocação de pessoal com base nos diagnósticos (prerrogativa da SUBSEGOP/Alta Gestão)'], ARRAY['Metodologia e instrumentos de mapeamento definidos pelo ICTDEC', 'Patrocínio institucional da SUBSEGOP', 'Processos mapeados (AE 2.1.6) como insumo para definição de competências por função', 'Regimentos internos atualizados (AE 2.1.5) como base formal para o mapeamento de funções', 'Comprometimento dos órgãos subordinados com o fornecimento de informações fidedignas', 'Capacidade da ESDEC para absorver as demandas de desenvolvimento geradas pelos diagnósticos']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.7') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'SUOP'), 'principal');
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.7') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'SUBSEGOP'), 'aval');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '2.1.7'), 2, 'ICTDEC — COEXECUTOR METODOLÓGICO',
    'ICTDEC', 'COEXECUTOR METODOLÓGICO', 'O ICTDEC é responsável pela formulação do modelo de gestão por competências e pela elaboração dos instrumentos de mapeamento. Sua contribuição abrange a definição da metodologia, a condução técnica dos mapeamentos de funções e competências, e a produção dos diagnósticos de lacuna. O ICTDEC não executa as ações de desenvolvimento decorrentes (capacitações, trilhas) nem decide sobre alocação de pessoal — essas competências pertencem, respectivamente, à ESDEC e à Alta Gestão.',
    ARRAY['Elaborar o modelo de gestão por competências, definindo conceitos, taxonomia e instrumentos de mapeamento', 'Conduzir o mapeamento das funções da estrutura da SEDEC, em articulação com os órgãos subordinados', 'Definir as competências necessárias e desejáveis para cada função, a partir dos processos mapeados (AE 2.1.6)', 'Elaborar instrumentos para mapeamento das competências da força de trabalho', 'Produzir os diagnósticos de lacuna (competências requeridas vs. existentes) e encaminhá-los à SUOP', 'Fornecer insumos técnicos à ESDEC para orientação das trilhas de aprendizagem e da grade de capacitações (AE 3.1.1)', 'Formalizar acordos de cooperação técnica com parceiros externos para capacitações que a ESDEC não consiga ofertar internamente, a partir das lacunas identificadas', 'Revisar e atualizar a metodologia com base na experiência dos primeiros ciclos de aplicação'], ARRAY['Não executar ações de capacitação ou desenvolvimento (competência da ESDEC)', 'Não decidir sobre alocação, movimentação ou avaliação de desempenho de pessoal (prerrogativa da SUBSEGOP/Alta Gestão)', 'Não coordenar a implantação do modelo nos órgãos (competência da SUOP)', 'Não mapear competências sem dados de processos (dependência da AE 2.1.6)'], ARRAY['Processos mapeados (AE 2.1.6) como insumo para definição de competências por função', 'Regimentos internos atualizados (AE 2.1.5) que reflitam as funções e atribuições de cada setor', 'Coordenação da SUOP para mobilização dos órgãos', 'Informações fidedignas dos órgãos subordinados sobre funções e força de trabalho', 'Disponibilidade de acesso ao efetivo do CBMERJ para aplicação do mapeamento de competências, considerando que a maior parte da força de trabalho da Secretaria integra a Corporação', 'Capacidade da ESDEC para absorver os insumos e traduzi-los em ações de desenvolvimento']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.7') AND ordem = 2), (SELECT id FROM setores WHERE codigo = 'ICTDEC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '2.1.7'), 3, 'ESDEC — COEXECUTORA DE DESENVOLVIMENTO',
    'ESDEC', 'COEXECUTORA DE DESENVOLVIMENTO', 'A ESDEC é a instância que traduz os diagnósticos de lacuna em ações concretas de desenvolvimento. O mapeamento de competências só gera valor institucional quando as lacunas identificadas se transformam em trilhas de aprendizagem, capacitações e programas de desenvolvimento. Sem essa conexão, o modelo fica restrito ao diagnóstico, sem produzir mudança efetiva na capacidade da força de trabalho.',
    ARRAY['Receber os diagnósticos de lacuna produzidos pelo ICTDEC e utilizá-los como insumo para a grade de capacitações (AE 3.1.1)', 'Estruturar trilhas de aprendizagem orientadas pelas competências requeridas para cada função', 'Adequar a oferta de cursos e programas de desenvolvimento às prioridades identificadas nos mapeamentos', 'Identificar competências cuja capacitação não pode ser ofertada internamente e articular, em conjunto com o ICTDEC, a busca de parceiros externos para suprir essas lacunas', 'Registrar e reportar a evolução das capacitações realizadas, permitindo a atualização dos diagnósticos de lacuna'], ARRAY['Não definir quais competências são requeridas para cada função (competência do ICTDEC com base nos processos mapeados)', 'Não conduzir o mapeamento de competências da força de trabalho (competência do ICTDEC)', 'Não decidir sobre alocação ou movimentação de pessoal (prerrogativa da SUBSEGOP/Alta Gestão)'], ARRAY['Diagnósticos de lacuna produzidos pelo ICTDEC, com priorização clara das competências mais críticas', 'Recursos e capacidade operacional para ampliar ou adequar a grade de cursos', 'Articulação com a SUOP para liberação do efetivo dos órgãos para participação nas capacitações', 'Formalização de acordos de cooperação técnica com parceiros externos pelo ICTDEC, para capacitações que a ESDEC não consiga ofertar internamente']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.7') AND ordem = 3), (SELECT id FROM setores WHERE codigo = 'ESDEC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '2.1.7'), 4, 'Órgãos Subordinados (Direções, Coordenações, REDECs e demais setores) — FORNECEDORES DE INFORMAÇÃO E DESTINATÁRIOS',
    'Órgãos Subordinados (Direções, Coordenações, REDECs e demais setores)', 'FORNECEDORES DE INFORMAÇÃO E DESTINATÁRIOS', 'Os órgãos subordinados são a fonte das informações que alimentam o modelo (quais funções existem, quem as ocupa, quais competências possuem) e, simultaneamente, os destinatários do modelo implantado (aplicação na gestão cotidiana do setor). Sua participação ativa no fornecimento de dados fidedignos é condição para que o diagnóstico reflita a realidade e gere ações de desenvolvimento pertinentes.',
    ARRAY['Fornecer informações sobre as funções exercidas no setor e as competências da força de trabalho', 'Validar os mapeamentos de funções e competências elaborados pelo ICTDEC, atestando a correspondência com a realidade operacional', 'Aplicar o modelo de gestão por competências na gestão cotidiana do setor (orientação de desenvolvimento, identificação de necessidades)', 'Liberar efetivo para participação nas capacitações orientadas pelos diagnósticos de lacuna', 'Sinalizar mudanças nas funções ou nas demandas de competência decorrentes de alterações nos processos (conexão com AE 2.1.6)'], ARRAY['Não definir metodologia ou instrumentos de mapeamento (competência do ICTDEC)', 'Não criar modelos paralelos de avaliação de competências', 'Não omitir ou distorcer informações sobre a força de trabalho (a qualidade do modelo depende da fidedignidade dos dados)'], ARRAY['Metodologia clara e objetiva, com instrumentos acessíveis e compatíveis com a rotina dos setores', 'Confidencialidade adequada dos dados individuais de competência (o modelo é de desenvolvimento, não punitivo)', 'Retorno sobre os diagnósticos realizados e as ações de desenvolvimento previstas', 'Capacitações efetivamente disponíveis para as lacunas identificadas (ESDEC)']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.7') AND ordem = 4), (SELECT id FROM setores WHERE codigo = 'ORGAOS_SUBORDINADOS'), 'principal');
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.7') AND ordem = 4), (SELECT id FROM setores WHERE codigo = 'REDECS'), 'participante');
INSERT INTO fundamentacoes (acao_estrategica_id, introducao, conclusao)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '2.1.7'), 'A ausência de gestão por competências gera três efeitos operacionais:', 'Esta ação enfrenta esses efeitos ao estabelecer um modelo integrado de mapeamento, diagnóstico e desenvolvimento que conecta funções a competências, competências a lacunas e lacunas a ações de capacitação, criando um ciclo contínuo de alinhamento entre capital humano e objetivos estratégicos.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.7')), 1, '1. Alocação desconectada da necessidade: Sem o conhecimento sistemático de quais competências cada função exige e quais a força de trabalho possui, a alocação de pessoal depende de critérios informais. Pessoas são designadas para funções sem que se verifique a aderência entre suas competências e as demandas da posição.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.7')), 2, '2. Capacitação sem foco estratégico: Sem diagnóstico de lacunas, as capacitações são orientadas por demandas avulsas ou por disponibilidade de oferta, e não pelas necessidades reais da instituição. Isso gera investimento em desenvolvimento que não necessariamente se traduz em melhoria de desempenho.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '2.1.7')), 3, '3. Ausência de referência para desenvolvimento: Sem um modelo que defina o que se espera de cada função, os servidores não têm clareza sobre quais competências devem desenvolver. O crescimento profissional fica sem direção institucional, dependendo exclusivamente de iniciativa individual.');

-- AE 3.1.1
INSERT INTO acoes_estrategicas (numero, nome, eixo_prioritario_id, objetivo_estrategico_id, estrategia_id,
    acoes_conectadas, descricao_oficial_completa, descricao_o_que, descricao_para_que, ancoragem,
    nota_arranjo_institucional, versao, data_elaboracao, elaboracao, proxima_fase)
VALUES ('3.1.1', 'Ampliar conhecimento e experiência do efetivo em proteção e defesa civil', (SELECT id FROM eixos_prioritarios WHERE codigo = 'EIXO_1'), (SELECT id FROM objetivos_estrategicos WHERE codigo = 'OE3'), (SELECT id FROM estrategias WHERE codigo = 'E3.1'),
    'AE 2.1.4 (Cultura organizacional) | AE 2.1.7 (Gestão por competências) | AE 3.1.2 (Avaliação de performance) | AE 6.1.4 (Extensão acadêmica)',
    'Estabelecimento de trilhas de educação continuada e intercâmbio de experiências práticas (O QUÊ), visando elevar o nível de especialização técnica da tropa para atuar com excelência tanto na gestão de riscos quanto no gerenciamento de desastres complexos (PARA QUÊ).',
    'Estabelecimento de trilhas de educação continuada e intercâmbio de experiências práticas.',
    'Elevar o nível de especialização técnica da tropa para atuar com excelência tanto na gestão de riscos quanto no gerenciamento de desastres complexos.',
    'O desenvolvimento do efetivo é o objetivo que conecta a governança à capacidade de execução. Instrumentos de gestão (processos, projetos, indicadores) só geram resultado quando operados por profissionais com conhecimento técnico adequado. O Plano Estratégico 2024–2035 posiciona esta ação na Estratégia E3.1 (Desenvolvimento e adequação do efetivo), com iniciativas que incluem trilhas de educação continuada e estímulo ao autodesenvolvimento. A ação tem dependência direta da gestão por competências (AE 2.1.7): os diagnósticos de lacuna produzidos pela AE 2.1.7 orientam o que capacitar; a presente AE trata de como capacitar. Sem essa conexão, as capacitações tendem a ser orientadas por demandas avulsas ou por disponibilidade de oferta, e não pelas necessidades reais da instituição. O OE 3 reforça que o fator crítico de sucesso é a liderança patrocinando uma cultura pautada no desenvolvimento pessoal e profissional.',
    NULL,
    '1.0',
    '2026-02-14',
    'ICTDEC/DAEAD',
    'Fase 2 — Desdobramento Orientado (definição de entregas por setor)');
INSERT INTO destaques_estrategicos (acao_estrategica_id, titulo, header_contexto)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '3.1.1'), 'Capacitação Orientada por Diagnóstico', 'CONTEXTO — Trilhas Dirigidas + Autodesenvolvimento');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '3.1.1')), 1, 'label_conteudo', 'Capacitação dirigida', 'Trilhas de educação continuada estruturadas a partir dos diagnósticos de lacuna (AE 2.1.7), ofertadas pela ESDEC com apoio de parceiros externos formalizados pelo ICTDEC. Inclui cursos, oficinas e programas de desenvolvimento orientados pelas competências requeridas para cada função. O Curso GIRD-RJ (Gestão Integrada de Riscos e Desastres) é o produto estruturante desta dimensão, voltado à qualificação técnica dos agentes da SEDEC-RJ.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '3.1.1')), 2, 'label_conteudo', 'Autodesenvolvimento', 'Estímulo ao autodesenvolvimento por meio de orientação sobre percursos formativos disponíveis e reconhecimento institucional do esforço individual de qualificação. A instituição deve sinalizar com clareza quais competências valoriza e oferecer condições para que o servidor busque seu próprio desenvolvimento.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '3.1.1')), 3, 'label_conteudo', 'Contratação de capacitações externas', 'Quando o diagnóstico de competências indicar necessidades que não possam ou não sejam convenientes de serem supridas diretamente pela ESDEC, a SUOP, com apoio da SUBSEGOP, pode patrocinar a contratação de cursos especializados junto a fornecedores externos, assegurando que o investimento esteja alinhado às prioridades estratégicas.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '3.1.1')), 4, 'label_conteudo', 'Condição de sustentabilidade', 'Para que a capacitação gere impacto duradouro, é necessário que o efetivo tenha tempo protegido para participação e que as chefias setoriais compreendam o desenvolvimento como investimento institucional, não como concorrência à operação. A articulação da SUOP é determinante para garantir essa condição.');
