INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '4.1.1'), 1, 'SUOP (sob aval da SUBSEGOP) — LÍDER COORDENADOR',
    'SUOP (sob aval da SUBSEGOP)', 'LÍDER COORDENADOR', 'As demandas de infraestrutura são dispersas entre os órgãos da Secretaria e frequentemente concorrem entre si por recursos escassos. A SUOP, como instância de integração operacional, é quem consolida essas demandas, prioriza as intervenções conforme o impacto na missão institucional e articula com a SUBSEGAD a viabilização administrativa e orçamentária. Sem essa coordenação, as melhorias tendem a ocorrer de forma reativa (só quando a precariedade se torna crítica) e fragmentada (cada órgão busca soluções isoladas).',
    ARRAY['Consolidar as demandas de infraestrutura dos órgãos subordinados', 'Priorizar as intervenções conforme o impacto operacional e o alinhamento estratégico, submetendo à SUBSEGOP quando necessário', 'Articular com a SUBSEGAD a viabilização administrativa e orçamentária das intervenções priorizadas', 'Monitorar a execução das intervenções e reportar à SUBSEGOP o andamento'], ARRAY['Não executar obras, reformas ou aquisições', 'Não gerir contratos de manutenção predial ou de serviços de engenharia', 'Não aprovar orçamento para investimentos novos (prerrogativa da SUBSEGOP/Alta Gestão)'], ARRAY['Informações fidedignas dos órgãos subordinados sobre as necessidades de infraestrutura', 'Apoio administrativo da SUBSEGAD na tramitação e viabilização orçamentária', 'Disponibilidade orçamentária para as intervenções priorizadas']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '4.1.1') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'SUOP'), 'principal');
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '4.1.1') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'SUBSEGOP'), 'aval');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '4.1.1'), 2, 'SUBSEGAD — APOIO ADMINISTRATIVO',
    'SUBSEGAD', 'APOIO ADMINISTRATIVO', 'A SUBSEGAD apoia a tramitação administrativa das demandas de infraestrutura, fornecendo informações sobre viabilidade orçamentária e contribuindo para a instrução dos processos necessários. A SUBSEGAD não define prioridades de intervenção (prerrogativa da SUOP/SUBSEGOP) nem executa obras ou aquisições diretamente.',
    ARRAY['Apoiar a instrução e tramitação administrativa dos processos relativos às intervenções de infraestrutura priorizadas', 'Fornecer à SUOP informações sobre viabilidade orçamentária e prazos de tramitação administrativa', 'Apoiar na identificação de instrumentos administrativos adequados para viabilizar as intervenções (contratos, convênios, adesões)'], ARRAY['Não priorizar as intervenções de infraestrutura (competência da SUOP/SUBSEGOP)', 'Não executar obras, reformas ou aquisições', 'Não consolidar demandas operacionais dos órgãos (competência da SUOP)'], ARRAY['Priorização clara das intervenções pela SUOP, com chancela da SUBSEGOP', 'Disponibilidade orçamentária para execução das demandas', 'Especificações técnicas das necessidades fornecidas pelos órgãos demandantes']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '4.1.1') AND ordem = 2), (SELECT id FROM setores WHERE codigo = 'SUBSEGAD'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '4.1.1'), 3, 'Órgãos Subordinados (Direções, Coordenações, REDECs e demais setores) — DEMANDANTES E USUÁRIOS',
    'Órgãos Subordinados (Direções, Coordenações, REDECs e demais setores)', 'DEMANDANTES E USUÁRIOS', 'Os órgãos subordinados são os usuários das instalações e, por isso, os primeiros a identificar precariedades, inadequações e necessidades de melhoria. Sua participação é essencial tanto na identificação das demandas quanto na validação das soluções adotadas. Sem esse insumo, as intervenções correm o risco de não atender às necessidades reais de quem opera nas instalações.',
    ARRAY['Identificar e reportar à SUOP as necessidades de infraestrutura do setor, com descrição do impacto operacional', 'Fornecer especificações técnicas sobre as necessidades específicas do setor (espaço, equipamentos fixos, condições de armazenamento)', 'Validar a adequação das soluções executadas à rotina operacional do setor', 'Zelar pela conservação e manutenção preventiva das instalações sob sua responsabilidade'], ARRAY['Não executar obras ou reformas por conta própria sem submissão ao fluxo institucional', 'Não contratar serviços de infraestrutura diretamente (observar o fluxo institucional)', 'Não priorizar suas demandas acima das demais sem submissão à priorização institucional da SUOP'], ARRAY['Canal claro para reportar necessidades de infraestrutura (definido pela SUOP)', 'Retorno sobre a priorização das demandas (o setor deve saber se sua necessidade foi incorporada, quando será atendida ou por que foi postergada)', 'Intervenções executadas com qualidade e dentro do prazo']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '4.1.1') AND ordem = 3), (SELECT id FROM setores WHERE codigo = 'ORGAOS_SUBORDINADOS'), 'principal');
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '4.1.1') AND ordem = 3), (SELECT id FROM setores WHERE codigo = 'REDECS'), 'participante');
INSERT INTO fundamentacoes (acao_estrategica_id, introducao, conclusao)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '4.1.1'), 'A precariedade da infraestrutura física gera três efeitos operacionais:', 'Esta ação enfrenta esses efeitos ao instituir um fluxo estruturado de demanda, priorização e execução que conecta quem usa (Órgãos Subordinados), quem prioriza (SUOP) e quem apoia administrativamente (SUBSEGAD), substituindo a lógica reativa por planejamento.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '4.1.1')), 1, '1. Perda de produtividade e qualidade de vida no trabalho: Instalações inadequadas (espaço insuficiente, falta de ergonomia, problemas de salubridade) afetam diretamente a capacidade de trabalho das equipes. O impacto não é apenas físico: ambientes precários corroem a motivação e reforçam a percepção de desvalorização institucional.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '4.1.1')), 2, '2. Limitação da capacidade operacional: A ausência de espaços adequados impede a realização de atividades que a estratégia prevê: salas para oficinas de mapeamento de processos, espaços para capacitações presenciais, condições de armazenamento para equipamentos operacionais. A infraestrutura precária se torna gargalo para ações de outras AEs.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '4.1.1')), 3, '3. Intervenções reativas e fragmentadas: Sem um processo estruturado de identificação, priorização e execução, as melhorias de infraestrutura acontecem apenas quando a precariedade atinge nível crítico, em caráter emergencial, com custo mais alto e menor qualidade.');

-- AE 4.3.1
INSERT INTO acoes_estrategicas (numero, nome, eixo_prioritario_id, objetivo_estrategico_id, estrategia_id,
    acoes_conectadas, descricao_oficial_completa, descricao_o_que, descricao_para_que, ancoragem,
    nota_arranjo_institucional, versao, data_elaboracao, elaboracao, proxima_fase)
VALUES ('4.3.1', 'Melhorar sistemas tecnológicos necessários à produtividade da defesa civil estadual', (SELECT id FROM eixos_prioritarios WHERE codigo = 'EIXO_1'), (SELECT id FROM objetivos_estrategicos WHERE codigo = 'OE4'), (SELECT id FROM estrategias WHERE codigo = 'E4.3'),
    'AE 4.1.1 (Infraestrutura física) | AE 4.2.1 (Recursos materiais) | AE 4.4.1 (Serviços contratados)',
    'Modernização e aquisição de ativos tecnológicos e soluções de software, abrangendo desde ferramentas de produtividade corporativa transversal até sistemas técnicos para setores especializados (O QUÊ), visando automatizar rotinas, eliminar gargalos operacionais e elevar a eficiência e a capacidade de entrega das equipes (PARA QUÊ).',
    'Modernização e aquisição de ativos tecnológicos e soluções de software, abrangendo desde ferramentas de produtividade corporativa transversal até sistemas técnicos para setores especializados.',
    'Automatizar rotinas, eliminar gargalos operacionais e elevar a eficiência e a capacidade de entrega das equipes.',
    'Os sistemas tecnológicos são a infraestrutura digital que suporta a execução das atividades da Secretaria. O Plano Estratégico 2024–2035 posiciona esta ação na Estratégia E4.3, com duas frentes: otimizar os sistemas de informação existentes e prover soluções para desenvolvimento e modernização da rede tecnológica. A ação abrange desde ferramentas de produtividade corporativa transversal até sistemas técnicos especializados. A ação é complementar às demais AEs do OE 4, que tratam respectivamente de infraestrutura física (AE 4.1.1), recursos materiais (AE 4.2.1) e serviços contratados (AE 4.4.1).',
    NULL,
    '1.0',
    '2026-02-14',
    'ICTDEC/DAEAD',
    'Fase 2 — Desdobramento Orientado (definição de entregas por setor)');
INSERT INTO destaques_estrategicos (acao_estrategica_id, titulo, header_contexto)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '4.3.1'), 'Automação Orientada por Processo', 'CONTEXTO — Tecnologia a Serviço do Processo, Não o Contrário');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '4.3.1')), 1, 'label_conteudo', 'Princípio', 'A automação só deve ocorrer após o processo estar compreendido e estável. Automatizar um processo confuso ou mal definido apenas faz o erro acontecer mais rápido. Por isso, quando aplicável, os mapeamentos de processos (AE 2.1.6) orientam o desenho das soluções tecnológicas — mas a liderança de cada projeto de automação é do setor dono do processo, não do ICTDEC.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '4.3.1')), 2, 'label_conteudo', 'Duas frentes', 'Ferramentas transversais: soluções de produtividade corporativa que atendem a múltiplos órgãos (comunicação, gestão de tarefas, colaboração). A SUOP coordena a priorização e padronização.
Sistemas especializados: soluções técnicas voltadas a setores específicos (monitoramento, análise de dados, alerta). A demanda parte do setor que opera o sistema, com coordenação da SUOP.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '4.3.1')), 3, 'label_conteudo', 'Contexto atual', 'A Secretaria opera com carência de recursos e baixa maturidade tecnológica institucional. A estratégia atual prioriza soluções gratuitas adaptadas ao estágio de maturidade corrente, com evolução gradual conforme a capacidade orçamentária e a consolidação dos processos.');
