INSERT INTO panoramico_setores (panoramico_linha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM panoramico_linhas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '4.4.1') AND ordem = 3), (SELECT id FROM setores WHERE codigo = 'ÓRGÃOS_SUBORDINADOS'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '4.4.1'), 1, 'SUOP (sob aval da SUBSEGOP) — LÍDER COORDENADOR',
    'SUOP (sob aval da SUBSEGOP)', 'LÍDER COORDENADOR', 'Os contratos de serviços devem refletir as necessidades reais da operação, o que exige um olhar transversal sobre as demandas dos órgãos. A SUOP consolida essas demandas, prioriza as contratações conforme o impacto operacional e monitora se os contratos vigentes continuam aderentes às necessidades da Secretaria. Sem essa coordenação, os contratos tendem a se renovar automaticamente sem revisão crítica, perpetuando serviços que já não correspondem às demandas atuais.',
    ARRAY['Consolidar as demandas de serviços contratados dos órgãos subordinados', 'Priorizar as contratações e renovações conforme o impacto operacional e o alinhamento estratégico', 'Avaliar periodicamente a aderência dos contratos vigentes às necessidades operacionais, propondo ajustes ou substituições', 'Articular com a SUBSEGOP a viabilização orçamentária das contratações priorizadas', 'Monitorar o desempenho dos serviços contratados com base nas informações dos órgãos usuários'], ARRAY['Não instruir processos administrativos de contratação', 'Não gerir contratos administrativamente', 'Não aprovar investimentos novos (prerrogativa da SUBSEGOP/Alta Gestão)'], ARRAY['Informações fidedignas dos órgãos subordinados sobre necessidades e desempenho dos serviços contratados', 'Apoio administrativo da SUBSEGAD na tramitação e nas informações sobre vigência contratual', 'Disponibilidade orçamentária para as contratações priorizadas']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '4.4.1') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'SUOP'), 'principal');
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '4.4.1') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'SUBSEGOP'), 'aval');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '4.4.1'), 2, 'SUBSEGAD — APOIO ADMINISTRATIVO',
    'SUBSEGAD', 'APOIO ADMINISTRATIVO', 'A SUBSEGAD apoia a tramitação administrativa dos processos de contratação, fornecendo informações sobre viabilidade orçamentária, vigência dos contratos vigentes e instrumentos administrativos disponíveis. A SUBSEGAD não define prioridades de contratação (prerrogativa da SUOP/SUBSEGOP).',
    ARRAY['Apoiar a instrução e tramitação administrativa dos processos de contratação e renovação', 'Fornecer à SUOP informações sobre vigência contratual, prazos de renovação e viabilidade orçamentária', 'Apoiar na identificação de instrumentos administrativos adequados (contratos, adesões, atas de registro de preços)'], ARRAY['Não priorizar contratações (competência da SUOP/SUBSEGOP)', 'Não consolidar demandas operacionais dos órgãos (competência da SUOP)', 'Não fiscalizar a execução dos serviços contratados (competência do órgão fiscal designado)'], ARRAY['Priorização clara das contratações pela SUOP, com chancela da SUBSEGOP', 'Disponibilidade orçamentária', 'Especificações técnicas das necessidades fornecidas pelos órgãos demandantes']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '4.4.1') AND ordem = 2), (SELECT id FROM setores WHERE codigo = 'SUBSEGAD'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '4.4.1'), 3, 'Órgãos Subordinados (Direções, Coordenações, REDECs e demais setores) — DEMANDANTES, FISCAIS E USUÁRIOS',
    'Órgãos Subordinados (Direções, Coordenações, REDECs e demais setores)', 'DEMANDANTES, FISCAIS E USUÁRIOS', 'Os órgãos subordinados são os usuários finais dos serviços contratados e, por isso, são quem melhor percebe a aderência (ou não) dos contratos às necessidades reais. Desempenham também o papel de fiscais da execução contratual, quando designados, assegurando que os serviços prestados correspondam ao contratado.',
    ARRAY['Identificar e reportar à SUOP as necessidades de serviços contratados, descrevendo o impacto operacional', 'Reportar inadequações, lacunas ou excessós nos contratos vigentes, subsidiando a revisão periódica conduzida pela SUOP', 'Fiscalizar a execução dos serviços contratados, quando designados como fiscal do contrato', 'Fornecer especificações técnicas para novas contratações ou adequações de contratos existentes'], ARRAY['Não contratar serviços por fora do fluxo institucional', 'Não alterar escopo contratual sem submissão ao processo formal', 'Não renovar contratos automaticamente sem avaliação crítica da aderência às necessidades atuais'], ARRAY['Canal claro para reportar necessidades e inadequações de serviços contratados (definido pela SUOP)', 'Retorno sobre a priorização das demandas (o setor deve saber o status da sua solicitação)', 'Apoio para o exercício da fiscalização contratual (orientação, instrumentos, canal de escalonamento)', 'Serviços contratados com qualidade e prazo compatíveis com a necessidade operacional']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
