INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '4.4.1') AND ordem = 3), (SELECT id FROM setores WHERE codigo = 'REDECS'), 'participante');
INSERT INTO fundamentacoes (acao_estrategica_id, introducao, conclusao)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '4.4.1'), 'A gestão deficiente da rede de serviços contratados gera três efeitos operacionais:', 'Esta ação enfrenta esses efeitos ao instituir um fluxo de revisão periódica dos contratos vigentes (aderência), uma priorização estratégica das contratações (alinhamento) e o fortalecimento da fiscalização contratual pelos órgãos usuários (qualidade da execução).');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '4.4.1')), 1, '1. Contratos desalinhados com a necessidade real: Quando os contratos são renovados sem revisão crítica, perpetuam-se serviços que não correspondem mais às demandas atuais. A Secretaria paga por serviços subutilizados enquanto áreas críticas permanecem desassistidas.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '4.4.1')), 2, '2. Fragilidade na prontidão operacional: A defesa civil opera em dois regimes — normalidade e crise. Contratos que atendem apenas à rotina administrativa podem ser insuficientes nos picos de demanda gerados por desastres. Sem planejamento estratégico do portfólio, a Secretaria fica vulnerável nos momentos de maior exigência.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '4.4.1')), 3, '3. Lentidão e baixa qualidade na instrução: Processos de contratação mal instruídos geram atrasos, impugnações e contratos com especificações insuficientes. O resultado é um ciclo de retrabalho que consome tempo administrativo e compromete a qualidade do serviço prestado.');

-- AE 6.1.3
INSERT INTO acoes_estrategicas (numero, nome, eixo_prioritario_id, objetivo_estrategico_id, estrategia_id,
    acoes_conectadas, descricao_oficial_completa, descricao_o_que, descricao_para_que, ancoragem,
    nota_arranjo_institucional, versao, data_elaboracao, elaboracao, proxima_fase)
VALUES ('6.1.3', 'Implantar parcerias para implementação de projetos de pesquisa e extensão técnico-científica (Estrutura SEDEC e COMDECs)', (SELECT id FROM eixos_prioritarios WHERE codigo = 'EIXO_3'), (SELECT id FROM objetivos_estrategicos WHERE codigo = 'OE6'), (SELECT id FROM estrategias WHERE codigo = 'E6.1'),
    'AE 6.1.1 (Ecossistema colaborativo) | AE 6.1.4 (Extensão acadêmica) | AE 6.1.6 (Redes de pesquisa) | AE 1.1.3 (Fontes de recursos)',
    'Promoção da integração técnica e do alinhamento entre a SEDEC e as Defesas Civis Municipais (COMDECs) para o desenvolvimento colaborativo de iniciativas de ciência e extensão (O QUÊ), visando solucionar gargalos operacionais específicos das pontas, promover o intercâmbio de saberes e fortalecer a coesão do Sistema Estadual através de uma rede de conhecimento aplicado (PARA QUÊ).',
    'Promoção da integração técnica e do alinhamento entre a SEDEC e as Defesas Civis Municipais (COMDECs) para o desenvolvimento colaborativo de iniciativas de ciência e extensão.',
    'Solucionar gargalos operacionais específicos das pontas, promover o intercâmbio de saberes e fortalecer a coesão do Sistema Estadual através de uma rede de conhecimento aplicado.',
    'A produção de conhecimento aplicado é uma das missões centrais do ICTDEC e condição para que a Defesa Civil evolua de uma atuação baseada em empirismo para uma prática fundamentada em ciência. O Plano Estratégico 2024–2035 posiciona esta ação na Estratégia E6.1 (Estruturação da atividade de C&T), com foco específico na integração entre a SEDEC e as Defesas Civis Municipais (COMDECs) para o desenvolvimento colaborativo de pesquisa e extensão. A iniciativa indicada é a disseminação de informação técnico-científica para pesquisadores da estrutura SEDEC e do SIEPDEC. A ação se articula com a AE 6.1.1 (ecossistema colaborativo com IES e ICTs), a AE 6.1.4 (extensão acadêmica), a AE 6.1.6 (redes de pesquisa) e a AE 1.1.3 (fontes de recursos), pois parcerias de pesquisa são também vetor de captação de recursos via editais de fomento.',
    NULL,
    '1.0',
    '2026-02-14',
    'ICTDEC/DAEAD',
    'Fase 2 — Desdobramento Orientado (definição de entregas por setor)');
INSERT INTO destaques_estrategicos (acao_estrategica_id, titulo, header_contexto)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.1.3'), 'Pesquisa Orientada por Problema Operacional', 'CONTEXTO — Ciência a Serviço da Operação');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.1.3')), 1, 'label_conteudo', 'Princípio', 'As parcerias de pesquisa e extensão desta ação devem ser orientadas pelos gargalos operacionais reais da Secretaria e dos municípios — não por interesses acadêmicos desconectados da prática. O ICTDEC articula as parcerias e o CEPEDEC executa os projetos de pesquisa, assegurando que os produtos gerados tenham aplicabilidade direta nas atividades de proteção e defesa civil.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.1.3')), 2, 'label_conteudo', 'Ciclo de valor', '1. Identificar o problema: gargalos operacionais percebidos pela SUOP, pelos órgãos subordinados ou pelas COMDECs. As REDECs, como órgãos vocacionados para a interface com os municípios, são o canal natural de coleta dessas demandas.
2. Formalizar a parceria: o ICTDEC articula com IES, ICTs e parceiros externos a formalização de acordos de cooperação técnica, com aprovação da SUBSEGAD e parecer da ASSEJUR.
3. Executar a pesquisa: o CEPEDEC conduz os projetos de pesquisa, em articulação com os parceiros formalizados.
4. Transferir o conhecimento: os produtos da pesquisa são disseminados para a estrutura SEDEC e para o SIEPDEC, transformando-se em insumo para melhoria de práticas, protocolos e políticas.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.1.3')), 3, 'label_conteudo', 'Risco a evitar', 'Parcerias acadêmicas que geram produção científica desconectada da operação. Se a pesquisa não resolver problema real ou não gerar produto aplicável, o investimento institucional em parceria não se justifica. A orientação por problema operacional é o filtro de priorização.');
INSERT INTO panoramico_linhas (acao_estrategica_id, ordem, setor_display, papel, sintese_contribuicao, nao_faz)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.1.3'), 1, 'ICTDEC', 'Líder e Articulador', 'Formaliza parcerias; coordena portfólio de projetos; dissemina produtos da pesquisa', 'Não executa pesquisa diretamente; não define prioridades sem a SUOP');
