VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.1.3'), 2, 'CEPEDEC', 'Executor de Pesquisa', 'Conduz projetos de pesquisa; produz conhecimento aplicado; articula com parceiros acadêmicos na execução', 'Não formaliza parcerias; não define linhas de pesquisa isoladamente');
