INSERT INTO panoramico_setores (panoramico_linha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM panoramico_linhas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.2.1') AND ordem = 4), (SELECT id FROM setores WHERE codigo = 'ÓRGÃOS_SUBORDINADOS'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.2.1'), 1, 'CEPEDEC — LÍDER DE PD&I',
    'CEPEDEC', 'LÍDER DE PD&I', 'O CEPEDEC é o órgão de pesquisa da Secretaria e, nesta ação, assume a liderança do ciclo de Pesquisa, Desenvolvimento e Inovação institucional. Compete-lhe transformar problemas organizacionais em projetos de PD&I, conduzir a pesquisa aplicada, desenvolver e testar soluções (novos produtos, serviços ou processos) e entregá-las validadas para incorporação pela SUOP. O CEPEDEC utiliza como matéria-prima tanto os gargalos sinalizados pelos órgãos subordinados quanto os diagnósticos produzidos pelo ICTDEC nas ações de governança e gestão.',
    ARRAY['Conduzir o ciclo de PD&I institucional: pesquisa aplicada, desenvolvimento, prototipagem e validação de soluções', 'Transformar problemas organizacionais identificados pelos órgãos e pelo ICTDEC em projetos de inovação', 'Consultar referências externas, parceiros acadêmicos (AE 6.1.3) e conhecimento interno sistematizado (AE 6.3.2) para fundamentar as soluções', 'Entregar soluções validadas à SUOP para incorporação na rotina institucional', 'Manter portfólio de projetos de inovação em andamento, com registro de resultados e lições aprendidas'], ARRAY['Não definir prioridades de inovação sem validação da SUOP quanto à aderência aos problemas operacionais prioritários', 'Não implantar inovações diretamente nos órgãos (a incorporação é coordenada pela SUOP)', 'Não produzir inovações desconectadas de problemas reais da instituição'], ARRAY['Sinalização de problemas e gargalos prioritários pela SUOP e pelos órgãos subordinados', 'Diagnósticos de governança e gestão produzidos pelo ICTDEC (mapeamento de processos, diagnósticos de competências, avaliações de desempenho)', 'Conhecimento tácito sistematizado (AE 6.3.2) como insumo para projetos de inovação', 'Parcerias acadêmicas formalizadas (AE 6.1.3) para suporte técnico-científico quando necessário', 'Recursos para custeio dos projetos de PD&I']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.2.1') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'CEPEDEC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.2.1'), 2, 'ICTDEC — EXECUTOR E PROVEDOR DE INSUMOS',
    'ICTDEC', 'EXECUTOR E PROVEDOR DE INSUMOS', 'O ICTDEC, ao conduzir as ações de melhoria de governança e gestão (mapeamento de processos, diagnósticos de competências, avaliações de desempenho, análise institucional), gera um volume significativo de informações sobre gargalos, redundâncias e oportunidades de melhoria. Esses diagnósticos são matéria-prima para o ciclo PD&I liderado pelo CEPEDEC. O ICTDEC não lidera a inovação, mas a alimenta com insumos estruturados e fomenta a organização das informações que permitem identificar onde a inovação pode gerar maior impacto.',
    ARRAY['Fornecer ao CEPEDEC os diagnósticos e dados produzidos nas ações de governança e gestão (AEs 2.1.1 a 2.1.7) como subsídio para projetos de inovação', 'Fomentar a organização e sistematização de informações das ações de melhoria que possam servir de subsídios para criação de inovações', 'Sinalizar ao CEPEDEC gargalos recorrentes identificados nos diagnósticos que demandem soluções inovadoras', 'Apoiar a validação técnica de inovações que incidam sobre processos de gestão e governança (assegurar aderência às metodologias institucionais)'], ARRAY['Não liderar o ciclo de PD&I (competência do CEPEDEC)', 'Não conduzir pesquisa aplicada para desenvolvimento de soluções (competência do CEPEDEC)', 'Não incorporar inovações nos órgãos (coordenação da SUOP)'], ARRAY['Ações de governança e gestão em execução (AEs do OE 2) gerando diagnósticos e dados utilizáveis', 'Canal claro de comunicação com o CEPEDEC para transferência dos insumos', 'Retroalimentação do CEPEDEC sobre quais insumos são mais úteis para o ciclo PD&I']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.2.1') AND ordem = 2), (SELECT id FROM setores WHERE codigo = 'ICTDEC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.2.1'), 3, 'SUOP (sob aval da SUBSEGOP) — ORIENTADOR ESTRATÉGICO',
    'SUOP (sob aval da SUBSEGOP)', 'ORIENTADOR ESTRATÉGICO', 'A SUOP conecta a inovação à operação. Compete-lhe validar a priorização dos projetos de PD&I, sinalizar os problemas operacionais mais urgentes e coordenar a incorporação das inovações validadas na rotina dos órgãos.',
    ARRAY['Validar a priorização dos projetos de PD&I propostos pelo CEPEDEC, assegurando alinhamento às necessidades operacionais', 'Sinalizar ao CEPEDEC os problemas operacionais prioritários da Secretaria', 'Coordenar a incorporação das inovações validadas na rotina dos órgãos subordinados, com suporte à adoção e à gestão da mudança (AE 2.1.4)', 'Apoiar a disseminação dos resultados do PD&I para os órgãos da Secretaria'], ARRAY['Não conduzir projetos de PD&I (competência do CEPEDEC)', 'Não desenvolver soluções inovadoras (competência do CEPEDEC)', 'Não definir metodologia de pesquisa (competência do CEPEDEC)'], ARRAY['Portfólio de projetos de PD&I organizado pelo CEPEDEC, com descrição do problema, solução proposta e produto esperado', 'Inovações entregues validadas e em formato incorporável à rotina (não apenas protótipos)']);
