INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.2.1'), 4, 'Órgãos Subordinados (Direções, Coordenações, REDECs e demais setores) — FONTE DE PROBLEMAS E DESTINATÁRIOS',
    'Órgãos Subordinados (Direções, Coordenações, REDECs e demais setores)', 'FONTE DE PROBLEMAS E DESTINATÁRIOS', 'Os órgãos subordinados desempenham duplo papel: são a fonte primária dos problemas que o PD&I deve resolver (gargalos, ineficiências, necessidades não atendidas) e os destinatários finais das inovações, que devem ser incorporadas à sua rotina.',
    ARRAY['Identificar e reportar gargalos operacionais, ineficiências e necessidades que possam ser endereçados por inovação', 'Participar da validação de protótipos e soluções em desenvolvimento, fornecendo feedback sobre aplicabilidade', 'Adotar as inovações incorporadas à rotina institucional, com apoio da SUOP na transição'], ARRAY['Não desenvolver soluções de PD&I isoladamente (a condução é do CEPEDEC)', 'Não rejeitar inovações validadas e incorporadas sem justificativa técnica fundamentada'], ARRAY['Comunicação prévia sobre inovações em desenvolvimento que afetarão o setor', 'Suporte à adoção (capacitação, transição gradual, canal de dúvidas) coordenado pela SUOP', 'Retorno sobre os resultados das inovações incorporadas (o setor deve saber se a mudança gerou o impacto esperado)']);
