VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.4.1'), 1, 'CEPEDEC', 'Líder da Busca de Soluções', 'Busca soluções internas e externas para os pontos de melhoria; avalia viabilidade; recomenda incorporação', 'Não identifica pontos de melhoria nos processos; não implanta nos órgãos');
