VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.4.1'), 2, 'ICTDEC', 'Identificador de Pontos de Melhoria', 'Identifica pontos de melhoria em processos implantados a partir da visão de DO e governança', 'Não busca soluções; não avalia viabilidade científica');
INSERT INTO panoramico_setores (panoramico_linha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM panoramico_linhas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.4.1') AND ordem = 2), (SELECT id FROM setores WHERE codigo = 'ICTDEC'), 'principal');
INSERT INTO panoramico_linhas (acao_estrategica_id, ordem, setor_display, papel, sintese_contribuicao, nao_faz)
