INSERT INTO panoramico_setores (panoramico_linha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM panoramico_linhas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.4.1') AND ordem = 3), (SELECT id FROM setores WHERE codigo = 'ÓRGÃOS_SUBORDINADOS'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.4.1'), 1, 'CEPEDEC — LÍDER DA BUSCA DE SOLUÇÕES',
    'CEPEDEC', 'LÍDER DA BUSCA DE SOLUÇÕES', 'O CEPEDEC é o órgão de pesquisa da Secretaria. Nesta ação, recebe do ICTDEC e dos órgãos a identificação de pontos de melhoria em produtos, processos e serviços já em operação e assume a responsabilidade de buscar soluções — seja no conhecimento produzido internamente (PD&I, saberes sistematizados), seja junto a parceiros externos (IES, ICTs, redes de pesquisa). O CEPEDEC avalia a viabilidade técnica das inovações incrementais identificadas, prioriza e recomenda a incorporação.',
    ARRAY['Receber os pontos de melhoria identificados pelo ICTDEC (a partir da análise de processos implantados) e pelos órgãos (a partir da experiência operacional)', 'Buscar soluções para os pontos de melhoria, tanto no conhecimento interno (AE 6.2.1, AE 6.3.2) quanto em fontes externas (AE 6.1.3, AE 6.1.6)', 'Avaliar a viabilidade técnica das inovações incrementais identificadas', 'Priorizar e recomendar a incorporação das melhorias, com indicação de impacto esperado', 'Manter portfólio atualizado de inovações incrementais mapeadas, com status de incorporação'], ARRAY['Não identificar pontos de melhoria nos processos implantados (competência do ICTDEC, com apoio dos órgãos)', 'Não implantar as melhorias diretamente nos órgãos (a adoção é do próprio setor, com apoio da SUOP quando necessário)', 'Não criar produtos ou processos novos nesta ação (inovação radical é competência da AE 6.2.1)'], ARRAY['Pontos de melhoria identificados pelo ICTDEC e sinalizados pelos órgãos como insumo primário', 'Conhecimento produzido pelo PD&I (AE 6.2.1) e sistematizado (AE 6.3.2) como base de soluções', 'Produção das IES e ICTs em cooperação técnica (AE 6.1.3) como fonte de soluções externas', 'Processos mapeados pela AE 2.1.6 como referência dos fluxos existentes']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.4.1') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'CEPEDEC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.4.1'), 2, 'ICTDEC — IDENTIFICADOR DE PONTOS DE MELHORIA',
    'ICTDEC', 'IDENTIFICADOR DE PONTOS DE MELHORIA', 'O ICTDEC tem como atribuição o desenvolvimento organizacional da Secretaria. No exercício dessa atribuição — especialmente no mapeamento e na análise de processos (AE 2.1.6), nos diagnósticos de competências (AE 2.1.7) e nas avaliações de desempenho —, o ICTDEC identifica pontos de melhoria em produtos, processos e serviços já implantados. Esses pontos são encaminhados ao CEPEDEC como insumo para a busca de soluções incrementais. O ICTDEC não busca as soluções nem avalia viabilidade científica — seu papel é fornecer a visão de governança que orienta onde a inovação incremental deve atuar.',
    ARRAY['Identificar pontos de melhoria em processos já implantados, a partir da análise de processos, diagnósticos de competências e avaliações de desempenho', 'Encaminhar ao CEPEDEC os pontos de melhoria identificados, com contextualização sobre o processo afetado e o impacto observado', 'Apoiar o CEPEDEC na compreensão do contexto organizacional dos pontos de melhoria, facilitando a busca de soluções aplicáveis'], ARRAY['Não buscar soluções para os pontos de melhoria (competência do CEPEDEC)', 'Não avaliar viabilidade científica das inovações incrementais (competência do CEPEDEC)', 'Não implantar melhorias nos órgãos (competência do próprio setor)'], ARRAY['Processos mapeados e em operação (AE 2.1.6) como base para identificação de pontos de melhoria', 'Diagnósticos de competências (AE 2.1.7) e avaliações de desempenho como fontes complementares', 'Capacidade do CEPEDEC para processar os pontos de melhoria encaminhados']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.4.1') AND ordem = 2), (SELECT id FROM setores WHERE codigo = 'ICTDEC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.4.1'), 3, 'Órgãos Subordinados (Direções, Coordenações e demais setores) — DETENTORES DOS PROCESSOS E DESTINATÁRIOS',
    'Órgãos Subordinados (Direções, Coordenações e demais setores)', 'DETENTORES DOS PROCESSOS E DESTINATÁRIOS', 'Os órgãos subordinados são os detentores dos produtos, processos e serviços que esta ação busca melhorar. Desempenham duplo papel: são a fonte de oportunidades (sinalizam onde há espaço para melhoria, onde os processos travam, onde as ferramentas estão defasadas) e os destinatários das inovações incrementais (adotam as melhorias recomendadas pelo CEPEDEC, incorporando-as à rotina). A validação de aplicabilidade no campo é essencial: só o órgão que opera o processo sabe se a melhoria proposta é viável na prática.',
    ARRAY['Sinalizar ao ICTDEC e ao CEPEDEC oportunidades de melhoria percebidas na operação de produtos, processos e serviços existentes', 'Validar a aplicabilidade das inovações incrementais propostas pelo CEPEDEC, fornecendo feedback sobre viabilidade operacional', 'Adotar as melhorias recomendadas e incorporá-las à rotina do setor', 'Reportar os resultados da incorporação (a melhoria gerou o impacto esperado?)'], ARRAY['Não conduzir o mapeamento técnico de inovações (competência do CEPEDEC)', 'Não avaliar viabilidade científica das melhorias (competência do CEPEDEC)', 'Não rejeitar melhorias validadas sem justificativa técnica fundamentada'], ARRAY['Recomendações do CEPEDEC com indicação clara de impacto esperado e requisitos de incorporação', 'Conformidade com o plano de incorporação assegurada pela SUOP (sob aval da SUBSEGOP)', 'Suporte à adoção quando a melhoria exigir capacitação ou adaptação de rotina', 'Retorno do CEPEDEC sobre os resultados reportados (retroalimentação do ciclo)']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.4.1') AND ordem = 3), (SELECT id FROM setores WHERE codigo = 'ORGAOS_SUBORDINADOS'), 'principal');
INSERT INTO fundamentacoes (acao_estrategica_id, introducao, conclusao)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.4.1'), 'A ausência de um mapeamento sistemático de inovações incrementais gera três efeitos:', 'Esta ação enfrenta esses efeitos ao instituir um identificação de pontos de melhoria pelo ICTDEC (visão de governança), busca de soluções pelo CEPEDEC (rigor técnico e articulação com parceiros) e validação pelos órgãos detentores dos processos (aplicabilidade).');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.4.1')), 1, '1. Soluções congeladas no tempo: Produtos, processos e serviços permanecem como foram criados, sem incorporar melhorias disponíveis. A Secretaria opera com ferramentas que poderiam ser atualizadas, mas não tem mecanismo para identificar e aplicar essas atualizações de forma sistemática.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.4.1')), 2, '2. Conhecimento produzido sem aplicação: As ações de PD&I e de sistematização de conhecimento geram produtos valiosos, mas sem um mecanismo de transferência para a operação, esse conhecimento não se traduz em melhoria concreta. A Secretaria investe em pesquisa e não colhe os frutos.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.4.1')), 3, '3. Substituição total como única via de mudança: Sem cultura de melhoria incremental, a única forma percebida de mudar é substituir tudo. Isso eleva custos, gera resistência e paralisa a inovação. A melhoria incremental é mais ágil, menos custosa e menos disruptiva.');

-- AE 6.5.1
INSERT INTO acoes_estrategicas (numero, nome, eixo_prioritario_id, objetivo_estrategico_id, estrategia_id,
    acoes_conectadas, descricao_oficial_completa, descricao_o_que, descricao_para_que, ancoragem,
    nota_arranjo_institucional, versao, data_elaboracao, elaboracao, proxima_fase)
VALUES ('6.5.1', 'Mapear o ciclo da informação: produção, organização/sistematização, armazenamento, disseminação, divulgação e uso na estrutura SEDEC', (SELECT id FROM eixos_prioritarios WHERE codigo = 'EIXO_1'), (SELECT id FROM objetivos_estrategicos WHERE codigo = 'OE6'), (SELECT id FROM estrategias WHERE codigo = 'E6.5'),
    'AE 6.5.2 (Metafonte) | AE 6.3.3 (Memória técnica) | AE 2.1.6 (Gestão de processos) | AE 4.3.1 (Sistemas tecnológicos)',
    'Estabelecimento de diretrizes de governança de dados e protocolos de fluxo informacional (O QUÊ), visando transformar dados dispersos em ativos de conhecimento institucional acessíveis, garantindo a preservação da memória técnica e a disponibilidade de informações confiáveis para subsidiar a tomada de decisão em todos os níveis (PARA QUÊ).',
    'Estabelecimento de diretrizes de governança de dados e protocolos de fluxo informacional.',
    'Transformar dados dispersos em ativos de conhecimento institucional acessíveis, garantindo a preservação da memória técnica e a disponibilidade de informações confiáveis para subsidiar a tomada de decisão em todos os níveis.',
    'A informação é o insumo mais crítico da Defesa Civil — da emissão de alertas à tomada de decisão estratégica. No entanto, não existe um mapeamento de como a informação é produzida, organizada, armazenada, disseminada e utilizada dentro da SEDEC. O Plano Estratégico 2024–2035 posiciona esta ação na Estratégia E6.5 (Aprimoramento da Gestão da Informação), com foco no estabelecimento de diretrizes de governança de dados e protocolos de fluxo informacional. A iniciativa indicada é identificar atores, dispositivos legais, sistemas de armazenamento e organização, sistemas de disseminação “entre os pares”, divulgação “para todos os públicos” e modos de apropriação (uso e aplicação) da informação. A ação é pré-requisito para a AE 6.5.2 (Metafonte) e complementa a AE 6.3.3 (memória técnica).',
    NULL,
    '1.0',
    '2026-02-16',
    'ICTDEC/DAEAD',
    'Fase 2 — Desdobramento Orientado (definição de entregas por setor)');
INSERT INTO destaques_estrategicos (acao_estrategica_id, titulo, header_contexto)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.5.1'), 'Quem Não Conhece seu Fluxo Não Governa sua Informação', 'CONTEXTO — O Ciclo Invisível');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.5.1')), 1, 'label_conteudo', 'Princípio', 'Toda instituição produz, organiza, armazena, dissemina e usa informação. Na maioria, esse ciclo acontece de forma implícita e não governada: cada setor cria seus próprios fluxos, armazena onde consegue, dissemina como pode. O resultado é duplicação, perda, fragmentação e decisões tomadas com informação incompleta. Esta ação torna o ciclo visível e governável: mapeia quem produz, onde armazena, como dissemina, quem usa e com qual finalidade.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.5.1')), 2, 'label_conteudo', 'As seis etapas do ciclo', '1. Produção: quem gera a informação, em qual formato, com qual frequência.
2. Organização/Sistematização: como a informação é classificada, indexada e estruturada.
3. Armazenamento: onde a informação reside — sistemas, pastas, pen drives, e-mails, papel.
4. Disseminação: como a informação circula entre os pares (fluxo interno técnico).
5. Divulgação: como a informação chega a públicos externos (sociedade, parceiros, órgãos de controle).
6. Uso: como a informação é efetivamente apropriada e aplicada na tomada de decisão.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.5.1')), 3, 'label_conteudo', 'Risco a evitar', 'Mapear o ciclo como exercício diagnóstico sem produzir diretrizes de governança. O mapeamento é meio, não fim: deve resultar em protocolos claros de fluxo informacional, com responsáveis, formatos e ferramentas definidos.');
INSERT INTO panoramico_linhas (acao_estrategica_id, ordem, setor_display, papel, sintese_contribuicao, nao_faz)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.5.1'), 1, 'ICTDEC', 'Líder do Mapeamento e Governança', 'Mapeia o ciclo da informação; define diretrizes e protocolos de fluxo informacional', 'Não opera os sistemas; não produz conteúdo informacional dos setores');
INSERT INTO panoramico_setores (panoramico_linha_id, setor_id, tipo_participacao)
