INSERT INTO panoramico_linhas (acao_estrategica_id, ordem, setor_display, papel, sintese_contribuicao, nao_faz)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.5.1'), 2, 'CEPEDEC', 'Detentor de Fluxo Científico', 'Participa do mapeamento com seu fluxo próprio; adota diretrizes', 'Não define governança de dados para a Secretaria');
INSERT INTO panoramico_setores (panoramico_linha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM panoramico_linhas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.5.1') AND ordem = 2), (SELECT id FROM setores WHERE codigo = 'CEPEDEC'), 'principal');
INSERT INTO panoramico_linhas (acao_estrategica_id, ordem, setor_display, papel, sintese_contribuicao, nao_faz)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.5.1'), 3, 'Órgãos Subordinados', 'Detentores de Fluxos Informacionais', 'Participam do mapeamento; descrevem seus fluxos; adotam protocolos', 'Não definem diretrizes; não criam protocolos próprios');
INSERT INTO panoramico_setores (panoramico_linha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM panoramico_linhas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.5.1') AND ordem = 3), (SELECT id FROM setores WHERE codigo = 'ÓRGÃOS_SUBORDINADOS'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.5.1'), 1, 'ICTDEC — LÍDER DO MAPEAMENTO E DA GOVERNANÇA INFORMACIONAL',
    'ICTDEC', 'LÍDER DO MAPEAMENTO E DA GOVERNANÇA INFORMACIONAL', 'O ICTDEC tem como atribuição o desenvolvimento organizacional da Secretaria. A governança da informação é componente estruturante do desenvolvimento organizacional: sem saber como a informação flui, não há como governar processos, medir desempenho ou tomar decisões fundamentadas. Compete ao ICTDEC conduzir o mapeamento do ciclo completo da informação (produção, organização, armazenamento, disseminação, divulgação, uso) em toda a estrutura SEDEC e, a partir desse diagnóstico, definir as diretrizes de governança de dados e os protocolos de fluxo informacional.',
    ARRAY['Conduzir o mapeamento do ciclo da informação em toda a estrutura SEDEC, identificando atores, dispositivos legais, sistemas, fluxos e modos de uso', 'Diagnosticar lacunas, redundâncias, gargalos e vulnerabilidades no ciclo informacional atual', 'Definir diretrizes de governança de dados: padrões de classificação, formatos, políticas de acesso, responsabilidades e ciclos de atualização', 'Definir protocolos de fluxo informacional: quem produz, para quem dissemina, em qual formato, com qual frequência', 'Articular o mapeamento com a AE 6.3.3 (memória técnica) e a AE 6.5.2 (Metafonte), assegurando coerência entre as ações de gestão da informação'], ARRAY['Não operar os sistemas de informação dos setores (cada órgão opera os seus)', 'Não produzir o conteúdo informacional dos setores (cada órgão produz o seu)', 'Não implantar sistemas tecnológicos (competência da AE 4.3.1, com apoio da SUBSEGAD)'], ARRAY['Colaboração dos órgãos subordinados e do CEPEDEC na descrição de seus fluxos informacionais', 'Patrocínio da SUOP (sob aval da SUBSEGOP) para assegurar que as rotinas de mapeamento e a adoção dos protocolos sejam levadas a sério', 'Infraestrutura tecnológica existente como base do diagnóstico (AE 4.3.1)', 'Processos mapeados na AE 2.1.6 como referência complementar dos fluxos de trabalho']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.5.1') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'ICTDEC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.5.1'), 2, 'CEPEDEC — DETENTOR DE FLUXO CIENTÍFICO',
    'CEPEDEC', 'DETENTOR DE FLUXO CIENTÍFICO', 'O CEPEDEC é o principal produtor de informação técnico-científica da Secretaria e, como tal, detém um fluxo informacional próprio — da produção de pesquisas ao armazenamento de dados científicos, da disseminação entre pares à divulgação de resultados. Nesta ação, o CEPEDEC participa do mapeamento descrevendo seu ciclo informacional e, uma vez definidas as diretrizes pelo ICTDEC, adota os protocolos de governança de dados instituídos.',
    ARRAY['Participar do mapeamento descrevendo o ciclo informacional próprio: como produz, organiza, armazena, dissemina, divulga e utiliza a informação científica', 'Colaborar com o ICTDEC na identificação de especificidades do fluxo científico que exijam tratamento diferenciado nas diretrizes', 'Adotar os protocolos de governança de dados e fluxo informacional definidos pelo ICTDEC', 'Assegurar que a produção científica esteja acessível conforme as diretrizes de disseminação instituídas'], ARRAY['Não definir diretrizes de governança de dados para a Secretaria (competência do ICTDEC)', 'Não conduzir o mapeamento dos fluxos dos demais órgãos', 'Não criar protocolos informacionais próprios que contrariem as diretrizes institucionais'], ARRAY['Diretrizes de governança de dados definidas pelo ICTDEC com clareza e aplicabilidade', 'Respeito às especificidades do fluxo científico nas diretrizes instituídas', 'Infraestrutura de repositório adequada para a produção científica (AE 4.3.1)']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.5.1') AND ordem = 2), (SELECT id FROM setores WHERE codigo = 'CEPEDEC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.5.1'), 3, 'Órgãos Subordinados (Direções, Coordenações e demais setores) — DETENTORES DE FLUXOS INFORMACIONAIS',
    'Órgãos Subordinados (Direções, Coordenações e demais setores)', 'DETENTORES DE FLUXOS INFORMACIONAIS', 'Os órgãos subordinados são os detentores dos fluxos informacionais que esta ação busca mapear. Cada setor produz, organiza, armazena, dissemina e usa informação no exercício de suas atribuições — muitas vezes de forma implícita e não padronizada. Nesta ação, os órgãos participam do mapeamento descrevendo seus fluxos e, uma vez definidos os protocolos, adotam as diretrizes de governança informacional instituídas pelo ICTDEC.',
    ARRAY['Participar do mapeamento descrevendo o ciclo informacional do setor: como produz, organiza, armazena, dissemina e usa a informação', 'Identificar e reportar ao ICTDEC lacunas, redundâncias e gargalos percebidos nos fluxos informacionais do setor', 'Adotar os protocolos de governança de dados e fluxo informacional definidos pelo ICTDEC', 'Manter os fluxos informacionais do setor em conformidade com as diretrizes instituídas'], ARRAY['Não definir diretrizes de governança de dados (competência do ICTDEC)', 'Não criar protocolos informacionais próprios que contrariem as diretrizes institucionais', 'Não reter informações de interesse institucional exclusivamente no setor, sem fluxo de disseminação definido'], ARRAY['Diretrizes e protocolos definidos pelo ICTDEC com clareza, aplicabilidade e respeito à realidade operacional dos setores', 'Patrocínio da SUOP (sob aval da SUBSEGOP) para assegurar que as rotinas de adoção dos protocolos sejam levadas a sério', 'Ferramentas e sistemas adequados para suportar os fluxos definidos (AE 4.3.1)', 'Capacitação quando necessária para operação dos novos protocolos']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.5.1') AND ordem = 3), (SELECT id FROM setores WHERE codigo = 'ORGAOS_SUBORDINADOS'), 'principal');
INSERT INTO fundamentacoes (acao_estrategica_id, introducao, conclusao)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '6.5.1'), 'A ausência de um mapeamento do ciclo da informação gera três efeitos:', 'Esta ação enfrenta esses efeitos ao instituir um mapeamento completo do ciclo da informação liderado pelo ICTDEC (visão de governança), alimentado pela descrição dos fluxos pelo CEPEDEC e pelos órgãos (realidade operacional), resultando em diretrizes e protocolos de fluxo informacional (governança aplicável).');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.5.1')), 1, '1. Dados dispersos e inacessíveis: A informação produzida pela Secretaria está espalhada em sistemas, pastas pessoais, e-mails e pen drives. Sem mapeamento, não se sabe o que existe, onde está nem quem é responsável. A mesma informação é produzida em duplicidade por setores diferentes, enquanto outras informações críticas não são produzidas por ninguém.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.5.1')), 2, '2. Decisões com informação incompleta: Gestores tomam decisões sem acesso à informação disponível porque não há fluxo de disseminação definido. A informação existe, mas não chega a quem precisa dela. O resultado é “achismo” em lugar de decisão fundamentada.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '6.5.1')), 3, '3. Memória técnica sem base informacional: As ações de preservação documental (AE 6.3.3) e de construção de repositórios (AE 6.5.2) dependem de saber o que existe e onde está. Sem o mapeamento do ciclo, essas ações operam no escuro.');

-- AE 7.1.2
INSERT INTO acoes_estrategicas (numero, nome, eixo_prioritario_id, objetivo_estrategico_id, estrategia_id,
    acoes_conectadas, descricao_oficial_completa, descricao_o_que, descricao_para_que, ancoragem,
    nota_arranjo_institucional, versao, data_elaboracao, elaboracao, proxima_fase)
VALUES ('7.1.2', 'Desenvolver atuação parlamentar para os interesses da proteção e defesa civil', (SELECT id FROM eixos_prioritarios WHERE codigo = 'EIXO_2'), (SELECT id FROM objetivos_estrategicos WHERE codigo = 'OE7'), (SELECT id FROM estrategias WHERE codigo = 'E7.1'),
    'AE 7.1.1 (Monitoramento do arcabouço normativo) | AE 2.1.5 (Revisão de normas internas) | AE 2.1.3 (Comunicação institucional)',
    'Fortalecimento da estrutura de assessoria parlamentar e capacitação técnica dos representantes institucionais (O QUÊ), visando elevar a autoridade da pasta nas discussões legislativas e assegurar a centralidade e a especificidade da agenda de Gestão de Riscos e Desastres (GRD) nos debates e alocações orçamentárias (PARA QUÊ).',
    'Fortalecimento da estrutura de assessoria parlamentar e capacitação técnica dos representantes institucionais.',
    'Elevar a autoridade da pasta nas discussões legislativas e assegurar a centralidade e a especificidade da agenda de Gestão de Riscos e Desastres (GRD) nos debates e alocações orçamentárias.',
    'A Defesa Civil depende de marcos legais adequados para operar com efetividade — desde a alocação orçamentária até os instrumentos jurídicos de atuação em emergência. No entanto, a agenda de Gestão de Riscos e Desastres (GRD) raramente é protagonista nas discussões legislativas. O Plano Estratégico 2024–2035 posiciona esta ação na Estratégia E7.1 (Aprimoramento de normas legais e infralegais), com foco no fortalecimento da estrutura de assessoria parlamentar e na capacitação técnica dos representantes institucionais. A iniciativa indicada é a criação de um núcleo de defesa civil para atividades parlamentares junto ao órgão responsável pela articulação com o Legislativo. A ação complementa a AE 7.1.1 (monitoramento do arcabouço normativo): enquanto lá se identifica o que precisa mudar, aqui se articula politicamente para que a mudança aconteça.',
    NULL,
    '1.0',
    '2026-02-16',
    'ICTDEC/DAEAD',
    'Fase 2 — Desdobramento Orientado (definição de entregas por setor)');
INSERT INTO destaques_estrategicos (acao_estrategica_id, titulo, header_contexto)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '7.1.2'), 'Argumento Técnico como Arma Política', 'CONTEXTO — Articulando com Fundamento');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '7.1.2')), 1, 'label_conteudo', 'Princípio', 'Atuação parlamentar eficaz não se faz apenas com prestígio político — exige argumentação técnica fundamentada. Quando a Defesa Civil se apresenta ao Legislativo municiada com dados, estudos, diagnósticos e propostas tecnicamente sólidas, eleva sua autoridade e diferencia sua agenda do lobby genérico. O suporte técnico institucional é o que confere credibilidade à atuação parlamentar e a torna sustentável independentemente de quem a conduza.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '7.1.2')), 2, 'label_conteudo', 'Duas frentes de atuação', 'Frente propositiva: elaborar e apresentar propostas legislativas fundamentadas — projetos de lei, emendas orçamentárias, requerimentos — que favoreçam a agenda de GRD no Estado.
Frente defensiva: monitorar e reagir a proposições legislativas que possam impactar negativamente a Defesa Civil — cortes orçamentários, alterações normativas prejudiciais, projetos que ignorem a especificidade da GRD.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '7.1.2')), 3, 'label_conteudo', 'Papel do suporte técnico', 'O órgão responsável pela articulação com o Legislativo executa a estratégia no parlamento. O ICTDEC fornece o substrato técnico que sustenta essa articulação: dados sobre impacto de desastres, indicadores de desempenho, diagnósticos de necessidade, análise técnica de projetos de lei, notas técnicas que fundamentem as posições institucionais e panoramas sobre necessidades de melhoria legislativa. A credibilidade da atuação parlamentar depende da qualidade desse suporte.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '7.1.2')), 4, 'label_conteudo', 'Risco a evitar', 'Atuação parlamentar desconectada da estratégia institucional. Demandas legislativas avulsas, sem alinhamento ao Plano Estratégico e às prioridades da SEDEC, dispersam esforço e podem gerar compromissos institucionais indesejados.');
INSERT INTO panoramico_linhas (acao_estrategica_id, ordem, setor_display, papel, sintese_contribuicao, nao_faz)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '7.1.2'), 1, 'ICTDEC', 'Provedor de Insumos Técnicos', 'Produz notas técnicas, análises e panoramas de necessidade legislativa que fundamentem a atuação parlamentar', 'Não articula com parlamentares; não executa estratégia no parlamento');
INSERT INTO panoramico_setores (panoramico_linha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM panoramico_linhas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '7.1.2') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'ICTDEC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '7.1.2'), 1, 'ICTDEC — PROVEDOR DE INSUMOS TÉCNICOS',
    'ICTDEC', 'PROVEDOR DE INSUMOS TÉCNICOS', 'O ICTDEC é responsável pela produção de inteligência estratégica e pela análise técnica que subsidia a tomada de decisão na Secretaria. Nesta ação, sua contribuição é fornecer o substrato técnico que confere credibilidade à atuação parlamentar: notas técnicas sobre projetos de lei, dados e indicadores sobre impacto de desastres, diagnósticos de necessidade legislativa, análises de impacto de proposições e panoramas sobre oportunidades de melhoria do arcabouço legal — desde reforços orçamentários até proposição de projetos de lei que propiciem melhora na atuação dos órgãos de proteção e defesa civil. O ICTDEC não articula com parlamentares nem executa a estratégia no parlamento — essa é competência do órgão responsável pela articulação com o Legislativo.',
    ARRAY['Produzir notas técnicas que fundamentem posições institucionais em discussões legislativas', 'Elaborar análises de impacto de projetos de lei e proposições que afetem a Defesa Civil', 'Fornecer dados, indicadores e diagnósticos que subsidiem emendas orçamentárias e requerimentos', 'Produzir panoramas sobre necessidades de melhoria legislativa, identificando lacunas no arcabouço legal que impactem a atuação da Secretaria', 'Capacitar tecnicamente os representantes institucionais que atuam junto ao Legislativo, fornecendo conteúdo e argumentação', 'Apoiar a elaboração de propostas legislativas com fundamentação técnica, quando demandado'], ARRAY['Não articular politicamente com parlamentares ou assessores (competência do órgão responsável pela articulação com o Legislativo)', 'Não negociar alocações orçamentárias ou compromissos legislativos (competência da Alta Gestão)', 'Não emitir posições institucionais sem validação da cadeia de comando', 'Não executar a estratégia de atuação no parlamento estadual ou federal'], ARRAY['Demandas claras do órgão de articulação parlamentar ou da Alta Gestão sobre temas a serem subsidiados tecnicamente', 'Acesso a dados e indicadores atualizados sobre impacto de desastres e desempenho institucional', 'Sinalizações da SUOP e dos órgãos subordinados sobre gargalos operacionais que demandem solução legislativa', 'Tempo hábil para elaboração de notas técnicas quando demandas legislativas surgirem', 'Definição do arranjo institucional responsável pela articulação parlamentar (ver Nota sobre Arranjo Institucional)']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '7.1.2') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'ICTDEC'), 'principal');
INSERT INTO fundamentacoes (acao_estrategica_id, introducao, conclusao)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '7.1.2'), 'A ausência de uma atuação parlamentar estruturada gera três efeitos:', 'Esta ação enfrenta esses efeitos ao estruturar uma atuação parlamentar municiada pelo suporte técnico do ICTDEC (fundamentação e panoramas de necessidade legislativa), executada pelo órgão responsável pela articulação com o Legislativo (estratégia no parlamento) e alimentada pelas demandas operacionais da SUOP e dos órgãos (realidade do campo).');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '7.1.2')), 1, '1. Invisibilidade legislativa: A agenda de Gestão de Riscos e Desastres não é protagonista nas discussões orçamentárias e normativas. Sem presença ativa no Legislativo, a Defesa Civil compete por recursos em desigualdade com agendas politicamente mais visíveis. Emendas orçamentárias e proposições legislativas favoráveis dependem de oportunismo, não de estratégia.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '7.1.2')), 2, '2. Vulnerabilidade normativa: Projetos de lei que impactam a Defesa Civil — cortes orçamentários, alterações de competência, regulamentações inadequadas — tramitam sem que a Secretaria tenha voz qualificada para reagir. A ausência de assessoria parlamentar técnica deixa a pasta exposta a decisões legislativas tomadas sem compreensão da especificidade da GRD.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '7.1.2')), 3, '3. Articulação dependente de pessoas: Quando a atuação parlamentar ocorre, tende a ser reativa e baseada em relações pessoais, não em argumentação técnica institucional. A eficácia da articulação depende de quem a conduz, não da qualidade do argumento nem do alinhamento estratégico. A saída de uma pessoa pode desmontar toda a articulação.');

-- AE 8.1.3
INSERT INTO acoes_estrategicas (numero, nome, eixo_prioritario_id, objetivo_estrategico_id, estrategia_id,
    acoes_conectadas, descricao_oficial_completa, descricao_o_que, descricao_para_que, ancoragem,
    nota_arranjo_institucional, versao, data_elaboracao, elaboracao, proxima_fase)
VALUES ('8.1.3', 'Fomentar a autonomia da rede de municípios fluminenses na implantação e custeamento de sistemas e métodos de alerta e alarme', (SELECT id FROM eixos_prioritarios WHERE codigo = 'EIXO_4'), (SELECT id FROM objetivos_estrategicos WHERE codigo = 'OE8'), (SELECT id FROM estrategias WHERE codigo = 'E8.1'),
    'AE 8.1.5 (Colaboração em prevenção) | AE 8.2.7 (Colaboração em preparação e resposta) | AE 10.1.2 (Projetos comunitários)',
    'Implementação de modelo de fomento que combine a expansão da infraestrutura (com atuação do estado em caráter excepcional e subsidiário) com a capacitação técnica municipal para a gestão e custeio da manutenção (O QUÊ), visando o progressivo cumprimento da competência legal prevista no art. 9º, XII da Lei 12.608/2012 e a sustentabilidade orçamentária de longo prazo dos sistemas de proteção (PARA QUÊ).',
    'Implementação de modelo de fomento que combine a expansão da infraestrutura (com atuação do Estado em caráter excepcional e subsidiário) com a capacitação técnica municipal para a gestão e custeio da manutenção.',
    'O progressivo cumprimento da competência legal prevista no art. 8º, V-B da Lei 12.608/2012 e a sustentabilidade orçamentária de longo prazo dos sistemas de proteção.',
    'A implantação e manutenção de sistemas de alerta e alarme é competência municipal (Art. 8º, V-B da Lei 12.608/2012). O Estado, ao assumir a manutenção dos sistemas de alarme por sirenes nos municípios, absorveu uma responsabilidade que não é sua e que compromete progressivamente sua capacidade orçamentária para investir no que lhe compete: monitoramento, alerta e cobertura tecnológica. O Plano Estratégico 2024–2035 posiciona esta ação na Estratégia E8.1, com foco na construção de um modelo que capacite os municípios a assumir essa responsabilidade de forma autônoma. Não se trata de abandonar os municípios, mas de devolver-lhes a competência legal com o suporte necessário para exercê-la.',
    NULL,
    '1.0',
    '2026-02-17',
    'ICTDEC/DAEAD',
    'Fase 2 — Desdobramento Orientado (definição de entregas por setor)');
INSERT INTO destaques_estrategicos (acao_estrategica_id, titulo, header_contexto)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3'), 'O Custo da Não-Decisão', 'CONTEXTO — Uma Responsabilidade que Não é Nossa');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3')), 1, 'label_conteudo', 'O problema', 'O Estado assumiu a manutenção de sistemas de alarme por sirenes que, por lei, são de competência municipal. Essa decisão, compreensível no contexto emergencial em que foi tomada, gerou um ciclo de dependência: os municípios não desenvolvem capacidade própria porque o Estado faz por eles; o Estado perde capacidade orçamentária para suas competências próprias (monitoramento, alerta, cobertura tecnológica) porque gasta com a manutenção de sistemas municipais. Cada ano sem decisão aprofunda o problema.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3')), 2, 'label_conteudo', 'As frentes de ação', 'Capacitação orçamentária: apoiar os municípios a ajustar seus orçamentos para absorver a despesa de manutenção dos sistemas de alarme.
Capacitação técnica: formar agentes municipais na elaboração de Estudos Técnicos Preliminares (ETP), Termos de Referência (TR) e gestão contratual para manutenção dos sistemas.
Sensibilização dos órgãos de controle externo: construir diálogo técnico com os órgãos de controle (TCE, MP) para sensibilizá-los quanto ao risco de a SEDEC continuar assumindo competência municipal, comprometendo seu orçamento e sua capacidade de atuação.
Acompanhamento de simulados: apoiar a realização de exercícios simulados de evacuação nas comunidades atendidas, assegurando que os sistemas implantados funcionem na prática.
Conscientização comunitária: preparar as comunidades para responder adequadamente aos sinais de alerta e alarme.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3')), 3, 'label_conteudo', 'O risco político', 'Esta ação exige posicionamento institucional firme. Devolver responsabilidade aos municípios pode gerar atritos políticos e questionamentos dos órgãos de controle — especialmente se a transição não for bem conduzida e documentada. A sensação de “abandono” é um risco real. Por isso a ação exige liderança da Alta Gestão (SUBSEGOP), que deve assumir o posicionamento e conduzir o diálogo com os órgãos de controle, respaldada pelo suporte técnico dos órgãos operacionais.');
INSERT INTO panoramico_linhas (acao_estrategica_id, ordem, setor_display, papel, sintese_contribuicao, nao_faz)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3'), 1, 'SUBSEGOP', 'Liderança Político-Institucional', 'Assume posicionamento; conduz diálogo com controle externo; respalda a transição', 'Não executa na ponta; não capacita');
INSERT INTO panoramico_setores (panoramico_linha_id, setor_id, tipo_participacao)
