INSERT INTO panoramico_setores (panoramico_linha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM panoramico_linhas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3') AND ordem = 7), (SELECT id FROM setores WHERE codigo = 'DGAC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3'), 1, 'SUBSEGOP — LIDERANÇA POLÍTICO-INSTITUCIONAL',
    'SUBSEGOP', 'LIDERANÇA POLÍTICO-INSTITUCIONAL', 'Esta ação envolve a devolução de uma competência legal aos municípios que o Estado assumiu ao longo dos anos. Essa transição não é apenas operacional — é política. Exige posicionamento institucional perante os órgãos de controle externo (TCE, MP), comunicação clara aos municípios e gestão do risco de percepção de “abandono”. A SUBSEGOP é o órgão que faz o elo entre os temas operacionais e políticos, sendo a instância adequada para assumir essa liderança, protegendo os órgãos operacionais de pressões políticas e conferindo legitimidade institucional ao processo.',
    ARRAY['Assumir o posicionamento institucional da SEDEC quanto à transferência progressiva de responsabilidade aos municípios', 'Conduzir o diálogo com os órgãos de controle externo (TCE, MP), sensibilizando-os quanto ao risco orçamentário e legal de a SEDEC manter competência municipal', 'Respaldar politicamente a SUOP e os órgãos operacionais na execução do modelo de transição', 'Assegurar que o processo de transição esteja documentado e auditável, prevenindo questionamentos futuros'], ARRAY['Não executar ações operacionais na ponta (competência dos órgãos setoriais)', 'Não definir padrões técnicos dos sistemas (competência do CEMADEN-RJ)', 'Não capacitar agentes municipais (competência da ESDEC)'], ARRAY['Suporte técnico dos órgãos operacionais para fundamentar o posicionamento perante os órgãos de controle', 'Documentação completa do modelo de transição (competências legais, cronograma, capacitações realizadas)', 'Apoio da ASSEJUR na validação jurídica do posicionamento institucional']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'SUBSEGOP'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3'), 2, 'SUOP (sob aval da SUBSEGOP) — COORDENADOR OPERACIONAL',
    'SUOP (sob aval da SUBSEGOP)', 'COORDENADOR OPERACIONAL', 'Ação que envolve múltiplos órgãos (CEMADEN-RJ, DGDEC, REDECs, ESDEC, DGAC) exige coordenação operacional centralizada. A SUOP comanda os órgãos setoriais e coordena ações multissetoriais. Nesta ação, compete-lhe definir a priorização de municípios, articular os recursos e assegurar que o modelo de fomento seja executado de forma integrada, sob o respaldo político da SUBSEGOP.',
    ARRAY['Coordenar a atuação integrada dos órgãos envolvidos, assegurando alinhamento de cronogramas e recursos', 'Definir critérios de priorização de municípios para implantação e capacitação', 'Articular recursos orçamentários e logísticos necessários', 'Monitorar o avanço da transferência de responsabilidade e reportar à SUBSEGOP'], ARRAY['Não assumir posicionamento político perante órgãos de controle (competência da SUBSEGOP)', 'Não executar na ponta (competência das REDECs e órgãos técnicos)', 'Não definir padrões técnicos (competência do CEMADEN-RJ)'], ARRAY['Respaldo político da SUBSEGOP para a execução do modelo', 'Colaboração dos órgãos setoriais', 'Disponibilidade orçamentária para ações de implantação subsidiária']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
