INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3') AND ordem = 2), (SELECT id FROM setores WHERE codigo = 'SUBSEGOP'), 'aval');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3'), 3, 'CEMADEN-RJ — REFERÊNCIA TÉCNICA',
    'CEMADEN-RJ', 'REFERÊNCIA TÉCNICA', 'O CEMADEN-RJ é responsável pelo monitoramento, emissão de alertas e apoio aos municípios em sistemas de alarme. Nesta ação, é a referência técnica: define padrões, especificações, requisitos de integração e protocolos de operação. Assessora a implantação e apoia a transição da manutenção. O fortalecimento da autonomia municipal libera o CEMADEN-RJ para recuperar sua capacidade de investimento no que lhe compete: monitoramento e cobertura tecnológica estadual.',
    ARRAY['Definir padrões técnicos, especificações e protocolos para sistemas de alerta e alarme municipais', 'Assessorar tecnicamente a implantação nos municípios priorizados', 'Apoiar a transição da manutenção para a gestão municipal', 'Fornecer à ESDEC os conteúdos técnicos para capacitação dos agentes municipais', 'Diagnosticar cobertura atual e lacunas nos sistemas do Estado'], ARRAY['Não custear sistemas municipais de forma permanente (competência municipal)', 'Não operar alarmes locais (competência municipal)', 'Não capacitar diretamente agentes municipais (competência da ESDEC)'], ARRAY['Recursos para expansão subsidiária (articulados pela SUOP)', 'REDECs como elo operacional na implantação', 'ESDEC como executora da capacitação com base nos conteúdos fornecidos']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3') AND ordem = 3), (SELECT id FROM setores WHERE codigo = 'CEMADEN_RJ'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3'), 4, 'DGDEC — ARTICULADOR INSTITUCIONAL',
    'DGDEC', 'ARTICULADOR INSTITUCIONAL', 'A DGDEC coordena as Regionais de Defesa Civil e é o canal formal da relação estado-município. Nesta ação, articula com as COMDECs a adesão ao modelo de fomento e coordena as REDECs na execução territorial.',
    ARRAY['Articular com as COMDECs a adesão ao modelo de fomento e os compromissos de transferência de responsabilidade', 'Coordenar as REDECs na execução desta ação no território', 'Formalizar os termos de cooperação com os municípios, com apoio da SUBSEGAD (formalização) e da ASSEJUR (validação jurídica)', 'Reportar à SUOP o status da adesão municipal e obstáculos identificados'], ARRAY['Não definir padrões técnicos (competência do CEMADEN-RJ)', 'Não capacitar agentes municipais (competência da ESDEC)', 'Não executar ações no território sem coordenação com as REDECs'], ARRAY['Critérios de priorização definidos pela SUOP', 'Padrões técnicos definidos pelo CEMADEN-RJ', 'Aprovação da SUBSEGAD para formalização de convênios e validação da ASSEJUR']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3') AND ordem = 4), (SELECT id FROM setores WHERE codigo = 'DGDEC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3'), 5, 'REDECs (Regionais de Defesa Civil) — EXECUTORAS NA PONTA',
    'REDECs (Regionais de Defesa Civil)', 'EXECUTORAS NA PONTA', 'As REDECs são o elo entre o Estado e os municípios no território. Nesta ação, acompanham a implantação, apoiam a realização de simulados de desocupação, coletam informações sobre adesão e dificuldades e atuam como elo operacional entre os órgãos estaduais e as COMDECs. Podem atuar em conjunto com vários órgãos (CEMADEN-RJ, ESDEC, DGAC), realizando a ação na ponta de forma integrada.',
    ARRAY['Acompanhar a implantação dos sistemas nos municípios de sua região', 'Apoiar a realização de simulados de desocupação nas comunidades atendidas', 'Coletar e reportar à DGDEC informações sobre adesão, dificuldades e necessidades', 'Atuar como elo operacional entre os órgãos estaduais e as COMDECs no território'], ARRAY['Não definir padrões técnicos (competência do CEMADEN-RJ)', 'Não capacitar formalmente agentes municipais (competência da ESDEC)', 'Não formalizar compromissos sem coordenação da DGDEC'], ARRAY['Orientações do CEMADEN-RJ para acompanhamento', 'Coordenação da DGDEC sobre prioridades e cronograma', 'Apoio dos órgãos técnicos conforme necessidade de cada município']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3') AND ordem = 5), (SELECT id FROM setores WHERE codigo = 'REDECS'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3'), 6, 'ESDEC — CAPACITADORA',
    'ESDEC', 'CAPACITADORA', 'A ESDEC capacita agentes do SIEPDEC-RJ. Nesta ação, seu papel é estruturante: formar agentes municipais não apenas na operação dos sistemas, mas também na dimensão administrativa da autonomia — ajuste orçamentário para absorver a despesa de manutenção, elaboração de Estudos Técnicos Preliminares (ETP), Termos de Referência (TR) e gestão contratual. Esse é o pilar que garante sustentabilidade: sem capacitação, a transferência de responsabilidade falha e o ciclo de dependência recomeça.',
    ARRAY['Estruturar programas de capacitação para agentes municipais abrangendo: operação e manutenção dos sistemas; ajuste orçamentário municipal para absorver a despesa; elaboração de ETP e TR; gestão contratual da manutenção', 'Executar a capacitação, preferencialmente no território (com apoio das REDECs), ou de forma centralizada', 'Avaliar o aprendizado e a eficácia da capacitação, retroalimentando o modelo'], ARRAY['Não definir padrões técnicos dos sistemas (competência do CEMADEN-RJ)', 'Não implantar ou operar sistemas', 'Não articular com municípios sobre adesão (competência da DGDEC)'], ARRAY['Conteúdos técnicos do CEMADEN-RJ como base da capacitação operacional', 'Demanda dimensionada pela SUOP com base na priorização de municípios', 'Apoio logístico das REDECs para capacitação no território']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3') AND ordem = 6), (SELECT id FROM setores WHERE codigo = 'ESDEC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3'), 7, 'DGAC — CONSCIENTIZAÇÃO COMUNITÁRIA',
    'DGAC', 'CONSCIENTIZAÇÃO COMUNITÁRIA', 'A DGAC é responsável pela percepção de riscos e conscientização comunitária. Nesta ação, seu papel é complementar e essencial: preparar as comunidades para responder aos sinais de alerta e alarme. Um sistema que toca sem que a população saiba reagir é infraestrutura ociosa.',
    ARRAY['Desenvolver e executar ações de conscientização comunitária sobre alertas, alarmes e procedimentos de evacuação', 'Articular com REDECs e COMDECs a realização de ações nos municípios com sistemas implantados', 'Produzir materiais de orientação para a população'], ARRAY['Não implantar ou operar sistemas', 'Não capacitar agentes municipais em operação técnica (competência da ESDEC)', 'Não definir padrões técnicos (competência do CEMADEN-RJ)'], ARRAY['Sistemas implantados e operacionais como pré-condição', 'Apoio das REDECs e COMDECs para execução territorial', 'Coordenação da SUOP para alinhamento de cronograma']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3') AND ordem = 7), (SELECT id FROM setores WHERE codigo = 'DGAC'), 'principal');
INSERT INTO fundamentacoes (acao_estrategica_id, introducao, conclusao)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3'), 'A situação atual configura um problema estrutural grave, cujo agravamento é proporcional ao tempo de não-decisão:', 'Esta ação enfrenta esses problemas com um modelo de transição liderado pela SUBSEGOP (posicionamento político-institucional), coordenado pela SUOP (integração operacional), referenciado pelo CEMADEN-RJ (padrões técnicos), articulado pela DGDEC (relação com municípios), executado pelas REDECs (ponta), capacitado pela ESDEC (formação orçamentária e técnica) e complementado pela DGAC (conscientização).');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3')), 1, '1. Inversão de competências com custo crescente: O Estado mantém sistemas de alarme por sirenes que são competência municipal (Art. 8º, V-B da Lei 12.608/2012). Cada ano de manutenção estadual aprofunda o comprometimento orçamentário da SEDEC, reduzindo a capacidade de investimento nas competências que são efetivamente do Estado: monitoramento meteorológico, hidrológico e geológico, emissão de alertas e cobertura tecnológica. A perda de capacidade orçamentária não é hipotética — já é realidade: o CEMADEN-RJ tem carência de investimento em instrumentos e tecnologias de monitoramento precisamente porque os recursos foram direcionados ao sistema de sirenes municipal.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3')), 2, '2. Dependência municipal institucionalizada: Os municípios que receberam sistemas implantados pelo Estado não desenvolveram capacidade técnica para operá-los, não elaboraram ETP e TR para contratação de manutenção e não ajustaram seus orçamentos para absorver essa despesa. A dependência não é acidental — é o resultado previsível de um modelo que implanta sem transferir capacidade. Quando o Estado reduz o suporte, os sistemas se degradam e a população fica desprotegida.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3')), 3, '3. Exposição a questionamentos dos órgãos de controle: A SEDEC gasta recursos orçamentários em competência que não é sua. Essa situação configura risco de questionamento por órgãos de controle externo (TCE, MP) — tanto pela execução de despesa fora de competência quanto pela possível omissão nas competências próprias. A não-decisão não elimina o risco — apenas atrasa o questionamento e agrava suas consequências.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.3')), 4, '4. Comunidades sem preparação: Mesmo onde os sistemas existem, a população muitas vezes não sabe o que os sinais significam nem como reagir. Sistemas de alarme sem conscientização comunitária e sem simulados regulares são infraestrutura ociosa — e a responsabilidade por qualquer falha de resposta recai sobre o Estado. Nota sobre Posicionamento Institucional e Risco de Omissão');

-- AE 8.1.5
INSERT INTO acoes_estrategicas (numero, nome, eixo_prioritario_id, objetivo_estrategico_id, estrategia_id,
    acoes_conectadas, descricao_oficial_completa, descricao_o_que, descricao_para_que, ancoragem,
    nota_arranjo_institucional, versao, data_elaboracao, elaboracao, proxima_fase)
VALUES ('8.1.5', 'Atuar em colaboração com os municípios nas ações de prevenção e mitigação', (SELECT id FROM eixos_prioritarios WHERE codigo = 'EIXO_4'), (SELECT id FROM objetivos_estrategicos WHERE codigo = 'OE8'), (SELECT id FROM estrategias WHERE codigo = 'E8.1'),
    'AE 8.1.3 (Autonomia em alerta/alarme) | AE 8.1.4 (Captação de recursos federais) | AE 8.2.7 (Colaboração em preparação e resposta) | AE 10.1.2 (Projetos comunitários)',
    'Estabelecimento de um regime de cooperação técnica e institucional multifacetado, abrangendo desde o suporte ao ordenamento territorial e normatização até a articulação para viabilização de medidas estruturais e não estruturais (O QUÊ), visando não apenas a instrumentalização da gestão local, mas a efetiva redução das vulnerabilidades e riscos preexistentes no território fluminense (PARA QUÊ).',
    'Estabelecimento de um regime de cooperação técnica e institucional multifacetado, abrangendo desde o suporte ao ordenamento territorial e normatização até a articulação para viabilização de medidas estruturais e não estruturais.',
    'Não apenas a instrumentalização da gestão local, mas a efetiva redução das vulnerabilidades e riscos preexistentes no território fluminense.',
    'A redução de vulnerabilidades e riscos no território fluminense depende de ações de prevenção e mitigação executadas nos municípios — mas a maioria não dispõe de capacidade técnica, científica e institucional para conduzi-las sozinha. O Plano Estratégico 2024–2035 posiciona esta ação na Estratégia E8.1, com foco no estabelecimento de um regime de cooperação técnica e institucional multifacetado: desde o suporte ao ordenamento territorial e à normatização até a articulação para viabilização de medidas estruturais e não estruturais. A iniciativa indicada é apoiar com recursos estaduais projetos de prevenção e mitigação. A ação mobiliza múltiplos órgãos da Defesa Civil Estadual, cada um na sua competência.',
    NULL,
    '1.0',
    '2026-02-17',
    'ICTDEC/DAEAD',
    'Fase 2 — Desdobramento Orientado (definição de entregas por setor)');
INSERT INTO destaques_estrategicos (acao_estrategica_id, titulo, header_contexto)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5'), 'Cooperação Multifacetada, Não Assistência Avulsa', 'CONTEXTO — Ação Integrada no Território');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5')), 1, 'label_conteudo', 'Princípio', 'Prevenção e mitigação não se fazem com ações isoladas. Exigem combinação de medidas estruturais (obras, infraestrutura, intervenções físicas) e medidas não estruturais (ordenamento territorial, normatização, capacitação, monitoramento, conscientização, estudos de risco). A cooperação do Estado com os municípios deve ser multifacetada e coordenada — cada órgão da SEDEC contribui com sua competência específica, e as REDECs integram essas contribuições na ponta.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5')), 2, 'label_conteudo', 'As facetas da cooperação', 'Alertas e panoramas de risco: CEMADEN-RJ fornece alertas geohidrometeorológicos e panoramas de risco que subsidiam a tomada de decisão municipal.
Engenharia e articulação para obras: Coordenadoria de Engenharia avalia intervenções físicas e articula com órgãos estaduais de infraestrutura a viabilização de medidas estruturais.
Meio ambiente: CEAMA conecta políticas ambientais à redução de riscos, articulando ações ambientais com impacto em defesa civil.
Suporte técnico-científico: ICTDEC (com CEPEDEC) produz estudos, metodologias e orienta municípios no mapeamento de riscos e vulnerabilidades.
Capacitação: ESDEC forma agentes municipais para atuar em prevenção e mitigação.
Conscientização comunitária: DGAC desenvolve percepção de risco e cultura preventiva nas comunidades.
Execução territorial: REDECs (via DREDEC) integram as frentes no território junto às COMDECs.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5')), 3, 'label_conteudo', 'Risco a evitar', 'Cooperação fragmentada. Se cada órgão atua isoladamente, sem coordenação da SUOP, o município recebe demandas descoordenadas e a ação integrada se desfaz em iniciativas avulsas sem impacto sistêmico.');
