INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5'), 1, 'SUOP (sob aval da SUBSEGOP) — COORDENADOR DA AÇÃO',
    'SUOP (sob aval da SUBSEGOP)', 'COORDENADOR DA AÇÃO', 'Ação multifacetada que envolve múltiplos órgãos da Defesa Civil Estadual exige coordenação centralizada. A SUOP comanda os órgãos setoriais e coordena ações multissetoriais. Nesta ação, compete-lhe priorizar municípios, articular recursos, alinhar cronogramas e assegurar que a cooperação chegue ao território de forma integrada — não como iniciativas avulsas de cada órgão.',
    ARRAY['Coordenar a atuação integrada dos órgãos envolvidos, assegurando alinhamento de cronogramas e recursos', 'Definir critérios de priorização de municípios para cooperação em prevenção e mitigação', 'Articular recursos orçamentários e logísticos para apoio a projetos municipais', 'Monitorar a execução da cooperação e seus resultados'], ARRAY['Não executar na ponta (competência das REDECs e órgãos técnicos)', 'Não definir padrões técnicos específicos (competência de cada órgão setorial)', 'Não articular diretamente com COMDECs (competência das REDECs via DREDEC)'], ARRAY['Colaboração dos órgãos setoriais na execução de suas partes', 'Informações sobre necessidades municipais coletadas pelas REDECs', 'Disponibilidade orçamentária para apoio a projetos de prevenção e mitigação']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'SUOP'), 'principal');
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'SUBSEGOP'), 'aval');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5'), 2, 'CEMADEN-RJ — ALERTAS E PANORAMAS DE RISCO',
    'CEMADEN-RJ', 'ALERTAS E PANORAMAS DE RISCO', 'O CEMADEN-RJ é responsável pelo monitoramento meteorológico, hidrológico e geológico do Estado e pela emissão de alertas. Nesta ação, fornece aos municípios alertas geohidrometeorológicos, panoramas de risco e recomendações de acionamento de sirenes, subsidiando a tomada de decisão municipal sobre medidas de prevenção e mitigação.',
    ARRAY['Fornecer alertas geohidrometeorológicos e panoramas de risco aos municípios', 'Emitir recomendações de acionamento de sirenes para as COMDECs', 'Disponibilizar informações sobre cenários de risco que subsidiem o planejamento de medidas preventivas'], ARRAY['Não produzir mapas de risco ou de suscetibilidade (competência do ICTDEC/CEPEDEC em orientação aos municípios)', 'Não fornecer dados brutos para mapeamento municipal', 'Não executar medidas estruturais ou não estruturais nos municípios', 'Não capacitar agentes municipais (competência da ESDEC)'], ARRAY['Infraestrutura de monitoramento adequada para gerar alertas confiáveis', 'REDECs como canal de transmissão de alertas e panoramas ao nível municipal']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5') AND ordem = 2), (SELECT id FROM setores WHERE codigo = 'CEMADEN_RJ'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5'), 3, 'Coordenadoria de Engenharia em Defesa Civil — SUPORTE ESTRUTURAL E ARTICULAÇÃO',
    'Coordenadoria de Engenharia em Defesa Civil', 'SUPORTE ESTRUTURAL E ARTICULAÇÃO', 'A Coordenadoria de Engenharia foi criada para pensar e coordenar ações de engenharia em defesa civil. Nesta ação, desempenha duplo papel: apoia municípios na avaliação técnica de intervenções físicas preventivas e articula com órgãos estaduais com competência na execução de obras (como SEINFRA e demais órgãos de infraestrutura) a viabilização de medidas estruturais. A capacidade atual do órgão é limitada (carência de quadro técnico), o que reforça a importância do papel articulador: mobilizar capacidade técnica onde ela existe no aparato estatal.',
    ARRAY['Apoiar municípios na avaliação técnica de intervenções físicas preventivas e de mitigação', 'Articular com órgãos estaduais de infraestrutura (SEINFRA e correlatos) a viabilização de medidas estruturais nos municípios', 'Emitir pareceres técnicos sobre viabilidade de medidas estruturais, quando demandado', 'Orientar projetos de obras de prevenção (contenção, drenagem, infraestrutura preventiva)'], ARRAY['Não executar obras (competência municipal ou de órgãos estaduais de infraestrutura)', 'Não monitorar riscos (competência do CEMADEN-RJ)', 'Não mapear áreas de risco (competência do ICTDEC/CEPEDEC em orientação aos municípios)'], ARRAY['Demandas priorizadas pela SUOP dado o quadro limitado de pessoal', 'Panoramas de risco fornecidos pelo CEMADEN-RJ para subsidiar avaliações', 'REDECs como canal de coleta de demandas municipais', 'Receptividade dos órgãos estaduais de infraestrutura para ações articuladas']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5') AND ordem = 3), (SELECT id FROM setores WHERE codigo = 'COORD_ENGENHARIA'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5'), 4, 'CEAMA (Coordenadoria Estadual de Ações de Meio Ambiente) — ARTICULAÇÃO AMBIENTAL',
    'CEAMA (Coordenadoria Estadual de Ações de Meio Ambiente)', 'ARTICULAÇÃO AMBIENTAL', 'A CEAMA coordena ações de meio ambiente com impacto em defesa civil. Nesta ação, conecta as políticas ambientais estaduais às ações de redução de riscos nos municípios, identificando onde intervenções ambientais (recuperação de encostas, gestão de recursos hídricos, proteção de APP) contribuem diretamente para a prevenção de desastres.',
    ARRAY['Identificar ações ambientais com impacto direto na redução de riscos de desastres nos municípios', 'Articular com órgãos ambientais estaduais ações conjuntas de prevenção', 'Orientar municípios sobre a integração de políticas ambientais e de defesa civil'], ARRAY['Não avaliar riscos de desastres (competência do CEMADEN-RJ para alertas e do ICTDEC/CEPEDEC para mapeamento)', 'Não executar obras de engenharia (competência da Coordenadoria de Engenharia e órgãos de infraestrutura)', 'Não capacitar agentes municipais (competência da ESDEC)'], ARRAY['Panoramas de risco do CEMADEN-RJ para identificar onde ações ambientais impactam', 'REDECs como elo territorial para implementação conjunta']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5') AND ordem = 4), (SELECT id FROM setores WHERE codigo = 'CEAMA'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5'), 5, 'ICTDEC (com CEPEDEC) — SUPORTE TÉCNICO-CIENTÍFICO',
    'ICTDEC (com CEPEDEC)', 'SUPORTE TÉCNICO-CIENTÍFICO', 'O ICTDEC, por meio do CEPEDEC, é o produtor de conhecimento científico e metodologias da Secretaria. Nesta ação, fornece o suporte técnico-científico que embasa a cooperação com os municípios: orientação no mapeamento de riscos e vulnerabilidades, produção de estudos aplicados e desenvolvimento de metodologias que subsidiam tanto as medidas estruturais quanto as não estruturais. O ICTDEC não atua diretamente nos municípios — seus produtos chegam ao território por meio dos órgãos operacionais e das REDECs.',
    ARRAY['Orientar municípios no mapeamento de riscos e vulnerabilidades, por meio do CEPEDEC', 'Produzir estudos e metodologias de avaliação de riscos aplicáveis ao território fluminense', 'Desenvolver pesquisas aplicadas que subsidiem medidas de prevenção e mitigação', 'Fornecer soluções técnicas e referências científicas aos órgãos operacionais para qualificar o suporte aos municípios', 'Apoiar a ESDEC com conteúdos técnico-científicos para a capacitação municipal em prevenção e mitigação'], ARRAY['Não atuar diretamente nos municípios (competência dos órgãos operacionais e das REDECs)', 'Não executar medidas estruturais ou não estruturais na ponta', 'Não emitir alertas geohidrometeorológicos (competência do CEMADEN-RJ)'], ARRAY['Demandas dos órgãos operacionais sobre necessidades de estudos e metodologias', 'Panoramas de risco do CEMADEN-RJ como insumo para pesquisas aplicadas', 'Parcerias com IES e ICTs (AE 6.1.3) como fonte complementar de conhecimento']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5') AND ordem = 5), (SELECT id FROM setores WHERE codigo = 'ICTDEC'), 'principal');
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5') AND ordem = 5), (SELECT id FROM setores WHERE codigo = 'CEPEDEC'), 'participante');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5'), 6, 'ESDEC — CAPACITADORA',
    'ESDEC', 'CAPACITADORA', 'A ESDEC capacita agentes do SIEPDEC-RJ. Nesta ação, forma agentes municipais para atuar em prevenção e mitigação: mapeamento de riscos, planejamento de medidas não estruturais, normatização local e ordenamento territorial. Os conteúdos técnicos são fornecidos pelo ICTDEC/CEPEDEC, pelo CEMADEN-RJ e pela Coordenadoria de Engenharia.',
    ARRAY['Estruturar e executar programas de capacitação para agentes municipais em prevenção e mitigação', 'Incluir conteúdos de mapeamento de riscos, normatização, ordenamento territorial e medidas não estruturais', 'Avaliar o aprendizado e retroalimentar o modelo de capacitação'], ARRAY['Não definir padrões técnicos (competência dos órgãos setoriais)', 'Não executar medidas de prevenção ou mitigação', 'Não articular com municípios sobre adesão (competência das REDECs via DREDEC)'], ARRAY['Conteúdos técnicos fornecidos pelo ICTDEC/CEPEDEC, CEMADEN-RJ e Coord. Engenharia', 'Demanda dimensionada pela SUOP', 'Apoio logístico das REDECs para capacitação no território']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5') AND ordem = 6), (SELECT id FROM setores WHERE codigo = 'ESDEC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5'), 7, 'DGAC — CONSCIENTIZAÇÃO COMUNITÁRIA',
    'DGAC', 'CONSCIENTIZAÇÃO COMUNITÁRIA', 'A DGAC desenvolve percepção de risco e cultura preventiva nas comunidades. Nesta ação, prepara a população para compreender os riscos do território e adotar comportamentos preventivos, complementando as medidas técnicas e estruturais com a dimensão humana da prevenção.',
    ARRAY['Desenvolver e executar ações de percepção de risco e cultura preventiva nas comunidades dos municípios priorizados', 'Articular com REDECs e COMDECs a realização de ações comunitárias', 'Produzir materiais de orientação para a população sobre prevenção e mitigação'], ARRAY['Não avaliar riscos tecnicamente (competência do CEMADEN-RJ e do ICTDEC/CEPEDEC)', 'Não executar medidas estruturais', 'Não capacitar agentes municipais (competência da ESDEC)'], ARRAY['Panoramas de risco do CEMADEN-RJ para direcionar ações comunitárias', 'Apoio das REDECs e COMDECs para execução territorial', 'Coordenação da SUOP para alinhamento de cronograma']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5') AND ordem = 7), (SELECT id FROM setores WHERE codigo = 'DGAC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5'), 8, 'REDECs (via DREDEC) — EXECUTORAS NA PONTA',
    'REDECs (via DREDEC)', 'EXECUTORAS NA PONTA', 'As REDECs são o elo entre o Estado e os municípios no território, coordenadas pela Diretoria das Regionais de Defesa Civil (DREDEC). Nesta ação, atuam como integradoras na ponta: recebem as contribuições dos órgãos especializados e operacionalizam a cooperação junto às COMDECs. Podem atuar em conjunto com vários órgãos (CEMADEN-RJ, Coord. Engenharia, CEAMA, ESDEC, DGAC), integrando as frentes no território. Coletam necessidades, reportam dificuldades e asseguram que o apoio estadual chegue ao município de forma coordenada.',
    ARRAY['Operacionalizar a cooperação no território, integrando as contribuições dos órgãos estaduais junto às COMDECs', 'Articular com as COMDECs a adesão ao regime de cooperação e identificar necessidades municipais', 'Coletar e reportar necessidades municipais em prevenção e mitigação', 'Apoiar a realização de ações de capacitação e conscientização no território', 'Acompanhar a implementação de medidas e reportar resultados'], ARRAY['Não definir padrões técnicos (competência dos órgãos setoriais)', 'Não capacitar formalmente agentes municipais (competência da ESDEC)', 'Não formalizar convênios sem aprovação da SUBSEGAD e validação da ASSEJUR'], ARRAY['Orientações dos órgãos técnicos para atuação no território', 'Priorização de municípios definida pela SUOP', 'Aprovação da SUBSEGAD para formalização de convênios e validação da ASSEJUR', 'Recursos logísticos para ações territoriais']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5') AND ordem = 8), (SELECT id FROM setores WHERE codigo = 'REDECS'), 'principal');
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5') AND ordem = 8), (SELECT id FROM setores WHERE codigo = 'DREDEC'), 'coordenador');
INSERT INTO fundamentacoes (acao_estrategica_id, introducao, conclusao)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5'), 'A cooperação do Estado com os municípios em prevenção e mitigação enfrenta três obstáculos estruturais:', 'Esta ação enfrenta esses obstáculos instituindo um regime de cooperação multifacetado coordenado pela SUOP, que integra as competências dos órgãos da Defesa Civil Estadual numa oferta coerente de apoio aos municípios — combinando alertas e panoramas de risco (CEMADEN-RJ), engenharia e articulação para obras (Coord. Engenharia), meio ambiente (CEAMA), conhecimento científico e orientação no mapeamento (ICTDEC/CEPEDEC), capacitação (ESDEC), conscientização (DGAC) e execução territorial (REDECs via DREDEC).');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5')), 1, '1. Municípios sem capacidade técnica: A maioria dos municípios fluminenses não dispõe de corpo técnico qualificado para conduzir mapeamentos de risco, elaborar planos de prevenção ou especificar medidas estruturais. Sem suporte estadual, as ações de prevenção e mitigação simplesmente não acontecem.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5')), 2, '2. Cooperação fragmentada: Quando o Estado atua, tende a fazê-lo de forma setorial e descoordenada: o CEMADEN-RJ emite alertas, a Coordenadoria de Engenharia opina sobre obras, a ESDEC capacita, a DGAC conscientiza — cada um no seu tempo, sem integração. O município recebe ações avulsas que não se somam num regime coerente de prevenção.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.1.5')), 3, '3. Ações reativas, não preventivas: A cultura institucional privilegia a resposta a desastres sobre a prevenção. Os municípios buscam o Estado após o desastre, não antes. A cooperação preventiva exige mudança de postura: antecipar-se ao evento, não reagir a ele.');

-- AE 8.2.5
INSERT INTO acoes_estrategicas (numero, nome, eixo_prioritario_id, objetivo_estrategico_id, estrategia_id,
    acoes_conectadas, descricao_oficial_completa, descricao_o_que, descricao_para_que, ancoragem,
    nota_arranjo_institucional, versao, data_elaboracao, elaboracao, proxima_fase)
VALUES ('8.2.5', 'Desenvolver conteúdo doutrinário para apoio às ações de preparação e resposta', (SELECT id FROM eixos_prioritarios WHERE codigo = 'EIXO_4'), (SELECT id FROM objetivos_estrategicos WHERE codigo = 'OE8'), (SELECT id FROM estrategias WHERE codigo = 'E8.2'),
    'AE 8.2.1 (Alinhamento operacional em resposta) | AE 8.2.7 (Colaboração com municípios) | AE 6.3.3 (Memória técnica) | AE 2.1.5 (Revisão de normas internas)',
    'Prestação de assessoria técnica e fomento às estratégias de planejamento de contingência, somada à mobilização de suporte operacional direto e complementar em situações de crise (O QUÊ), visando instrumentalizar a prontidão local e assegurar a suficiência da resposta estatal quando a capacidade de atendimento municipal for excedida (PARA QUÊ).',
    'Produção e atualização de manuais técnicos, POPs e guias de referência.',
    'A padronização tática das equipes e a preservação do conhecimento operacional acumulado pela instituição.',
    'A Secretaria acumula décadas de experiência operacional em preparação e resposta a desastres. No entanto, esse conhecimento está concentrado em pessoas, não em documentos. Manuais técnicos são escassos, POPs inexistem ou estão desatualizados, e os guias de referência não refletem as lições aprendidas nas operações mais recentes. O Plano Estratégico 2024–2035 posiciona esta ação na Estratégia E8.2 (Aprimoramento dos serviços de preparação e resposta), com foco na produção e atualização de conteúdo doutrinário. As iniciativas indicadas incluem a criação do manual de funcionamento dos Gabinetes Integrados de Gestão de Desastres (GIGD) e a produção de conteúdo de apoio aos municípios sobre estruturas de suporte a desastres.',
    NULL,
    '1.0',
    '2026-02-17',
    'ICTDEC/DAEAD',
    'Fase 2 — Desdobramento Orientado (definição de entregas por setor)');
INSERT INTO destaques_estrategicos (acao_estrategica_id, titulo, header_contexto)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5'), 'Transformar Experiência em Doutrina', 'CONTEXTO — Conhecimento Operacional Não Documentado');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5')), 1, 'label_conteudo', 'Princípio', 'A Secretaria sabe fazer. O problema é que esse saber está nas pessoas, não nos processos. Quando um operador experiente é transferido, promovido ou se aposenta, leva consigo o conhecimento que nunca foi documentado. A produção doutrinária é o mecanismo que transforma experiência individual em patrimônio institucional: manuais técnicos padronizam condutas, POPs garantem reprodutibilidade, guias de referência orientam quem ainda não tem a experiência.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5')), 2, 'label_conteudo', 'O ciclo da doutrina', 'Extração: capturar o conhecimento operacional dos órgãos que executam (DGDEC, CESTAD, REDECs) — os órgãos que operam detêm o conhecimento que precisa ser registrado.
Sistematização: o ICTDEC (via CEPEDEC) organiza, estrutura e valida tecnicamente o conteúdo extraído, transformando práticas em doutrina.
Formalização: produção dos manuais, POPs e guias em formato padronizado, com linguagem acessível e estrutura reproduzível.
Difusão: a ESDEC transforma o conteúdo doutrinário em material didático para capacitação das equipes e dos municípios.
Validação: a SUOP valida o produto final quanto ao alinhamento estratégico e à aplicabilidade operacional.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5')), 3, 'label_conteudo', 'Risco a evitar', 'Produzir doutrina desconectada da prática. Manuais elaborados sem a participação dos órgãos que operam não serão adotados. O ciclo exige que a extração do conhecimento venha dos órgãos operacionais.');
INSERT INTO panoramico_linhas (acao_estrategica_id, ordem, setor_display, papel, sintese_contribuicao, nao_faz)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5'), 1, 'ICTDEC (com CEPEDEC e ESDEC)', 'Líder da Produção Doutrinária', 'Sistematiza, formaliza e transforma conhecimento operacional em doutrina e material didático', 'Não é fonte primária do conhecimento operacional de resposta');
