INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5'), 1, 'ICTDEC (com CEPEDEC e ESDEC) — LÍDER DA PRODUÇÃO DOUTRINÁRIA',
    'ICTDEC (com CEPEDEC e ESDEC)', 'LÍDER DA PRODUÇÃO DOUTRINÁRIA', 'O ICTDEC é responsável pela produção de conhecimento e pelo desenvolvimento organizacional da Secretaria. Nesta ação, lidera o ciclo completo de produção doutrinária: o CEPEDEC sistematiza e formaliza o conhecimento operacional extraído dos órgãos que executam (DGDEC, CESTAD, REDECs), transformando práticas em manuais técnicos, POPs e guias de referência; a ESDEC transforma esse conteúdo em material didático para capacitação das equipes e dos municípios. O ICTDEC não é a fonte primária do conhecimento operacional de resposta — é quem o extrai, organiza, formaliza e difunde.',
    ARRAY['Conduzir o processo de extração do conhecimento operacional junto aos órgãos de preparação e resposta (DGDEC, CESTAD, REDECs)', 'Sistematizar e formalizar o conteúdo extraído em manuais técnicos, POPs e guias de referência, por meio do CEPEDEC', 'Transformar o conteúdo doutrinário em material didático para capacitação, por meio da ESDEC', 'Definir padrões de formato, linguagem e estrutura para os produtos doutrinários', 'Instituir ciclo de revisão periódica dos produtos, incorporando lições aprendidas e atualizações operacionais'], ARRAY['Não substituir a experiência operacional dos órgãos de resposta — a doutrina deve refletir a prática, não ser produzida isoladamente', 'Não validar o alinhamento estratégico dos produtos (competência da SUOP)', 'Não operar ações de preparação e resposta'], ARRAY['Colaboração ativa da DGDEC, do CESTAD e das REDECs no fornecimento de conteúdo operacional', 'Validação da SUOP quanto ao alinhamento estratégico e à aplicabilidade dos produtos', 'Acervo de lições aprendidas e memória técnica (AE 6.3.3) como insumo']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'ICTDEC'), 'principal');
