INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'ESDEC'), 'participante');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5'), 2, 'DGDEC — FONTE DE CONHECIMENTO DE RESPOSTA',
    'DGDEC', 'FONTE DE CONHECIMENTO DE RESPOSTA', 'A DGDEC é o órgão responsável pelas ações de resposta da Secretaria e detém o conhecimento operacional acumulado em assistência humanitária, logística de desastres e suporte às operações. Nesta ação, é fonte primária de conteúdo: fornece ao ICTDEC o saber operacional que será sistematizado em doutrina. Sem a participação da DGDEC, os manuais seriam documentos teóricos desconectados da realidade operacional.',
    ARRAY['Fornecer ao ICTDEC o conhecimento operacional acumulado nas ações de resposta e assistência humanitária', 'Participar ativamente do processo de extração de conhecimento, descrevendo fluxos, protocolos e práticas consolidadas', 'Validar operacionalmente os produtos doutrinários produzidos pelo ICTDEC, assegurando aderência à realidade de campo', 'Sinalizar necessidades de novos manuais, POPs ou guias com base nas lacunas percebidas na operação'], ARRAY['Não sistematizar nem formalizar a doutrina (competência do ICTDEC/CEPEDEC)', 'Não produzir material didático (competência da ESDEC)', 'Não definir padrões de formato dos produtos doutrinários'], ARRAY['Processo de extração conduzido pelo ICTDEC com metodologia clara e respeitável do tempo operacional', 'Retorno dos produtos finalizados para validação antes da publicação']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5') AND ordem = 2), (SELECT id FROM setores WHERE codigo = 'DGDEC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5'), 3, 'CESTAD — FONTE DE CONHECIMENTO DE GESTÃO DE DESASTRES',
    'CESTAD', 'FONTE DE CONHECIMENTO DE GESTÃO DE DESASTRES', 'O CESTAD é o órgão responsável pela coordenação das ações de resposta a desastres. Nesta ação, é fonte de conteúdo sobre gestão de crises, coordenação multisetorial em situação de desastre, protocolos de acionamento e fluxos de informação durante operações. Contribui ao lado de outros órgãos com experiência em resposta (DGDEC, REDECs, COMDECs) no fornecimento do conhecimento operacional que o ICTDEC sistematizará em doutrina.',
    ARRAY['Fornecer ao ICTDEC o conhecimento especializado sobre coordenação de resposta, gestão de crises e protocolos de acionamento', 'Participar do processo de extração de conhecimento, descrevendo fluxos de gestão de desastres e lições aprendidas em operações reais', 'Validar operacionalmente os produtos doutrinários relacionados à coordenação de resposta'], ARRAY['Não sistematizar nem formalizar a doutrina (competência do ICTDEC/CEPEDEC)', 'Não produzir material didático (competência da ESDEC)', 'Não definir padrões de formato dos produtos doutrinários'], ARRAY['Processo de extração conduzido pelo ICTDEC com metodologia clara', 'Retorno dos produtos finalizados para validação antes da publicação']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5') AND ordem = 3), (SELECT id FROM setores WHERE codigo = 'CESTAD'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5'), 4, 'REDECs (via DREDEC) — FONTE DE EXPERIÊNCIA DE CAMPO',
    'REDECs (via DREDEC)', 'FONTE DE EXPERIÊNCIA DE CAMPO', 'As REDECs são o elo entre o Estado e os municípios e acumulam experiência de campo diversificada: operam em contextos geográficos, climáticos e sociais distintos. Nesta ação, fornecem ao ICTDEC a perspectiva territorial que enriquece a doutrina: o que funciona numa REDEC pode não funcionar em outra, e essa diversidade precisa estar refletida nos produtos doutrinários. As REDECs também são as primeiras a identificar lacunas — situações em que não há manual, POP ou guia disponível.',
    ARRAY['Fornecer ao ICTDEC a experiência territorial e as lições aprendidas na ponta', 'Sinalizar lacunas doutrinárias percebidas na operação: situações sem manual, POP ou guia disponível', 'Validar a aplicabilidade territorial dos produtos doutrinários, garantindo que reflitam a diversidade de contextos'], ARRAY['Não sistematizar nem formalizar a doutrina (competência do ICTDEC/CEPEDEC)', 'Não produzir material didático (competência da ESDEC)'], ARRAY['Processo de extração conduzido pelo ICTDEC que respeite o tempo operacional e a diversidade regional', 'Retorno dos produtos para validação territorial antes da publicação']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5') AND ordem = 4), (SELECT id FROM setores WHERE codigo = 'REDECS'), 'principal');
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5') AND ordem = 4), (SELECT id FROM setores WHERE codigo = 'DREDEC'), 'coordenador');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5'), 5, 'SUOP (sob aval da SUBSEGOP) — VALIDADOR ESTRATÉGICO',
    'SUOP (sob aval da SUBSEGOP)', 'VALIDADOR ESTRATÉGICO', 'A SUOP comanda os órgãos setoriais e é responsável pelo alinhamento estratégico das ações operacionais. Nesta ação, valida os produtos doutrinários quanto ao alinhamento ao Plano Estratégico e à aplicabilidade operacional. A validação da SUOP confere legitimidade institucional aos produtos e assegura que a doutrina produzida seja efetivamente adotada pelos órgãos.',
    ARRAY['Validar os produtos doutrinários quanto ao alinhamento ao Plano Estratégico e às prioridades da SEDEC', 'Assegurar que os produtos tenham aplicabilidade operacional e sejam adotados pelos órgãos', 'Priorizar as demandas de produção doutrinária conforme as necessidades estratégicas', 'Arbitrar divergências entre órgãos sobre conteúdo doutrinário, quando necessário'], ARRAY['Não produzir conteúdo doutrinário (competência do ICTDEC)', 'Não extrair conhecimento operacional (competência dos órgãos de resposta)', 'Não transformar doutrina em capacitação (competência da ESDEC)'], ARRAY['Produtos apresentados pelo ICTDEC com tempo hábil para revisão', 'Informações dos órgãos sobre aderência dos produtos à realidade operacional']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5') AND ordem = 5), (SELECT id FROM setores WHERE codigo = 'SUOP'), 'principal');
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5') AND ordem = 5), (SELECT id FROM setores WHERE codigo = 'SUBSEGOP'), 'aval');
INSERT INTO fundamentacoes (acao_estrategica_id, introducao, conclusao)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5'), 'A ausência de conteúdo doutrinário atualizado gera três efeitos:', 'Esta ação enfrenta esses efeitos instituindo um ciclo de produção doutrinária liderado pelo ICTDEC (sistematização via CEPEDEC, difusão via ESDEC), alimentado pelos órgãos operacionais (DGDEC, CESTAD e REDECs como fontes de conhecimento) e validado pela SUOP (alinhamento estratégico e aplicabilidade).');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5')), 1, '1. Conhecimento preso em pessoas: A Secretaria depende da memória e da experiência de indivíduos para executar suas ações de preparação e resposta. Quando essas pessoas são transferidas, promovidas ou se aposentam, o conhecimento não permanece na instituição. A rotatividade — particularmente alta nas REDECs — amplifica esse efeito, gerando perda recorrente de capacidade operacional.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5')), 2, '2. Padronização inexistente: Sem manuais e POPs, cada equipe opera conforme sua própria interpretação. A mesma situação é tratada de formas diferentes por equipes diferentes. Isso gera inconsistência na resposta, dificulta a coordenação e impõe risco à população atendida.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.5')), 3, '3. Capacitação sem base doutrinária: A ESDEC forma agentes sem ter manuais de referência consolidados. O conteúdo da capacitação depende de quem ministra, não de uma doutrina institucional. Isso reproduz, na formação, o mesmo personalismo que afeta a operação.');

-- AE 8.2.7
INSERT INTO acoes_estrategicas (numero, nome, eixo_prioritario_id, objetivo_estrategico_id, estrategia_id,
    acoes_conectadas, descricao_oficial_completa, descricao_o_que, descricao_para_que, ancoragem,
    nota_arranjo_institucional, versao, data_elaboracao, elaboracao, proxima_fase)
VALUES ('8.2.7', 'Atuar em colaboração com os municípios nas ações de preparação e resposta', (SELECT id FROM eixos_prioritarios WHERE codigo = 'EIXO_4'), (SELECT id FROM objetivos_estrategicos WHERE codigo = 'OE8'), (SELECT id FROM estrategias WHERE codigo = 'E8.2'),
    'AE 8.1.5 (Colaboração em prevenção/mitigação) | AE 8.2.1 (Alinhamento operacional) | AE 8.2.5 (Conteúdo doutrinário) | AE 8.1.3 (Autonomia em alerta/alarme)',
    'Prestação de assessoria técnica e fomento às estratégias de planejamento de contingência, somada à mobilização de suporte operacional direto e complementar em situações de crise (O QUÊ), visando instrumentalizar a prontidão local e assegurar a suficiência da resposta estatal quando a capacidade de atendimento municipal for excedida (PARA QUÊ).',
    'Prestação de assessoria técnica e fomento às estratégias de planejamento de contingência, somada à mobilização de suporte operacional direto e complementar em situações de crise.',
    'Instrumentalizar a prontidão local e assegurar a suficiência da resposta estatal quando a capacidade de atendimento municipal for excedida.',
    'A capacidade de resposta a desastres no Estado depende da preparação dos municípios. Quando a capacidade municipal é excedida, o Estado é chamado a complementar — mas a qualidade dessa complementação depende de articulação prévia, não de improvisação durante a crise. O Plano Estratégico 2024–2035 posiciona esta ação na Estratégia E8.2, com duas dimensões complementares: a prestação de assessoria técnica para o planejamento de contingência municipal (preparação) e a mobilização de suporte operacional direto quando a capacidade local for excedida (resposta). A iniciativa indicada é apoiar com recursos estaduais projetos de preparação e resposta.',
    NULL,
    '1.0',
    '2026-02-17',
    'ICTDEC/DAEAD',
    'Fase 2 — Desdobramento Orientado (definição de entregas por setor)');
INSERT INTO destaques_estrategicos (acao_estrategica_id, titulo, header_contexto)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7'), 'Preparar Antes, Complementar Durante', 'CONTEXTO — Duas Dimensões da Colaboração');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7')), 1, 'label_conteudo', 'Princípio', 'A colaboração do Estado com os municípios em preparação e resposta opera em dois tempos distintos. Antes do desastre: assessoria técnica para o planejamento de contingência, capacitação de agentes municipais, realização de exercícios simulados e articulação interórgãos. Durante o desastre: mobilização de suporte operacional direto — logística, assistência humanitária, coordenação de resposta e integração multisetorial. A qualidade da resposta depende diretamente da qualidade da preparação.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7')), 2, 'label_conteudo', 'As frentes de ação', 'Assessoria ao planejamento de contingência: apoiar municípios na elaboração e atualização de planos de contingência, com definição de protocolos, responsabilidades e fluxos de acionamento.
Capacitação em preparação e resposta: formação de agentes municipais em coordenação de resposta, assistência humanitária e gestão de crises.
Suporte operacional complementar: mobilização de recursos estaduais (logística, assistência humanitária, equipes de resposta) quando a capacidade municipal for excedida.
Integração multisetorial: articulação de órgãos estaduais e entidades do GRAC para atuação coordenada em situação de crise.
Alertas e suporte em monitoramento: CEMADEN-RJ fornece alertas e panoramas de risco que orientam tanto a preparação quanto a resposta.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7')), 3, 'label_conteudo', 'Risco a evitar', 'Atuação exclusivamente reativa. Se a colaboração se limita ao suporte durante a crise, sem investimento na preparação, a resposta será sempre improvisada e a dependência municipal do Estado permanecerá elevada.');
