INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7'), 1, 'SUOP (sob aval da SUBSEGOP) — COORDENADOR DA AÇÃO',
    'SUOP (sob aval da SUBSEGOP)', 'COORDENADOR DA AÇÃO', 'Ação que envolve múltiplos órgãos da Defesa Civil Estadual exige coordenação centralizada. A SUOP comanda os órgãos setoriais e coordena ações multissetoriais. Nesta ação, compete-lhe priorizar municípios, articular recursos, alinhar cronogramas e assegurar que a colaboração em preparação e resposta chegue ao território de forma integrada.',
    ARRAY['Coordenar a atuação integrada dos órgãos envolvidos nas duas dimensões da ação (preparação e resposta)', 'Definir critérios de priorização de municípios para assessoria e suporte operacional', 'Articular recursos orçamentários e logísticos para apoio a projetos de preparação e resposta', 'Monitorar a execução da colaboração e seus resultados'], ARRAY['Não executar na ponta (competência das REDECs e órgãos operacionais)', 'Não definir protocolos técnicos de resposta (competência dos órgãos setoriais)', 'Não articular diretamente com COMDECs (competência das REDECs via DREDEC)'], ARRAY['Colaboração dos órgãos setoriais na execução de suas partes', 'Informações sobre necessidades municipais coletadas pelas REDECs', 'Disponibilidade orçamentária para apoio a projetos']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'SUOP'), 'principal');
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'SUBSEGOP'), 'aval');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7'), 2, 'DGDEC — RESPOSTA E ASSISTÊNCIA HUMANITÁRIA',
    'DGDEC', 'RESPOSTA E ASSISTÊNCIA HUMANITÁRIA', 'A DGDEC é o órgão responsável pelas ações de resposta e detém o conhecimento e a logística de assistência humanitária da Secretaria. Nesta ação, atua nas duas dimensões: na preparação, assessora municípios na elaboração de planos de contingência e na estruturação de suas capacidades de resposta; durante a crise, mobiliza suporte operacional direto — logística, produtos de assistência humanitária e equipes de apoio — quando a capacidade municipal for excedida.',
    ARRAY['Assessorar municípios na elaboração e atualização de planos de contingência', 'Mobilizar suporte operacional direto (logística, assistência humanitária, equipes) quando a capacidade municipal for excedida', 'Orientar municípios na estruturação de suas capacidades de resposta', 'Apoiar a realização de exercícios simulados nos municípios'], ARRAY['Não monitorar riscos (competência do CEMADEN-RJ)', 'Não capacitar formalmente agentes municipais (competência da ESDEC)', 'Não articular órgãos externos para resposta multisetorial (competência do GRAC)'], ARRAY['Alertas do CEMADEN-RJ para antecipar mobilização de recursos', 'REDECs como canal de execução territorial', 'Recursos orçamentários e logísticos articulados pela SUOP']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7') AND ordem = 2), (SELECT id FROM setores WHERE codigo = 'DGDEC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7'), 3, 'CESTAD — COORDENAÇÃO DE RESPOSTA',
    'CESTAD', 'COORDENAÇÃO DE RESPOSTA', 'O CESTAD é o órgão responsável pela coordenação das ações de resposta a desastres. Nesta ação, atua na preparação (estruturação de protocolos de coordenação, operação de estruturas de gestão de crises) e durante a resposta (coordenação da atuação integrada em situação de desastre, fluxos de informação e articulação entre os órgãos envolvidos).',
    ARRAY['Estruturar e manter atualizados os protocolos de coordenação de resposta', 'Operar as estruturas de gestão de crises durante desastres', 'Coordenar os fluxos de informação entre órgãos durante a resposta', 'Apoiar os municípios na estruturação de seus próprios mecanismos de coordenação de resposta'], ARRAY['Não prestar assistência humanitária direta (competência da DGDEC)', 'Não monitorar riscos (competência do CEMADEN-RJ)', 'Não capacitar formalmente agentes municipais (competência da ESDEC)'], ARRAY['Alertas do CEMADEN-RJ para ativação das estruturas de gestão de crises', 'Colaboração da DGDEC e das REDECs na execução da resposta coordenada', 'Protocolos do GRAC para integração multisetorial']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7') AND ordem = 3), (SELECT id FROM setores WHERE codigo = 'CESTAD'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7'), 4, 'CEMADEN-RJ — ALERTAS E SUPORTE EM CRISE',
    'CEMADEN-RJ', 'ALERTAS E SUPORTE EM CRISE', 'O CEMADEN-RJ é responsável pelo monitoramento e pela emissão de alertas geohidrometeorológicos. Nesta ação, fornece os alertas e panoramas de risco que orientam tanto a preparação quanto a resposta: na preparação, seus panoramas subsidiam o planejamento de contingência; durante a crise, seus alertas e recomendações de acionamento de sirenes orientam a tomada de decisão municipal e a mobilização estadual.',
    ARRAY['Fornecer alertas geohidrometeorológicos e panoramas de risco que subsidiem o planejamento de contingência municipal', 'Emitir alertas e recomendações de acionamento de sirenes durante eventos adversos', 'Disponibilizar informações sobre cenários de risco que orientem a mobilização de recursos de resposta'], ARRAY['Não coordenar a resposta a desastres (competência do CESTAD)', 'Não mobilizar logística ou assistência humanitária (competência da DGDEC)', 'Não produzir mapas de risco ou de suscetibilidade'], ARRAY['Infraestrutura de monitoramento adequada para gerar alertas confiáveis', 'Fluxos de comunicação definidos com o CESTAD e a DGDEC para acionamento tempestivo']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7') AND ordem = 4), (SELECT id FROM setores WHERE codigo = 'CEMADEN_RJ'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7'), 5, 'GRAC (Grupo de Ações Coordenadas) — INTEGRAÇÃO MULTISETORIAL',
    'GRAC (Grupo de Ações Coordenadas)', 'INTEGRAÇÃO MULTISETORIAL', 'O GRAC é responsável pela integração dos órgãos participantes do SIEPDEC-RJ para preparação e resposta a desastres, sob coordenação da SEDEC. Nesta ação, articula órgãos estaduais, entidades da Rede Salvar e demais integrantes do sistema para atuação coordenada. Na preparação, promove o entrosamento entre os órgãos e a definição de protocolos de atuação conjunta. Durante a resposta, viabiliza a mobilização multisetorial de recursos e capacidades.',
    ARRAY['Articular órgãos estaduais, entidades da Rede Salvar e demais integrantes do SIEPDEC-RJ para atuação coordenada em desastres', 'Promover exercícios de entrosamento entre os órgãos participantes', 'Definir protocolos de atuação conjunta para situações de desastre', 'Viabilizar a mobilização multisetorial de recursos e capacidades durante a resposta'], ARRAY['Não executar ações operacionais diretas de resposta (competência da DGDEC e do CESTAD)', 'Não assessorar municípios em planos de contingência (competência da DGDEC)', 'Não monitorar riscos (competência do CEMADEN-RJ)'], ARRAY['Definição clara das atribuições dos órgãos participantes', 'Protocolo de acionamento integrado ao CESTAD', 'Disponibilidade dos órgãos para participação em exercícios e na resposta']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7') AND ordem = 5), (SELECT id FROM setores WHERE codigo = 'GRAC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7'), 6, 'ESDEC — CAPACITADORA',
    'ESDEC', 'CAPACITADORA', 'A ESDEC capacita agentes do SIEPDEC-RJ. Nesta ação, forma agentes municipais em preparação e resposta a desastres: planejamento de contingência, coordenação de resposta, assistência humanitária e gestão de crises. Os conteúdos técnicos são fornecidos pela DGDEC, pelo CESTAD e pelo CEMADEN-RJ.',
    ARRAY['Estruturar e executar programas de capacitação para agentes municipais em preparação e resposta', 'Incluir conteúdos de planejamento de contingência, coordenação de resposta, assistência humanitária e gestão de crises', 'Avaliar o aprendizado e retroalimentar o modelo de capacitação'], ARRAY['Não assessorar municípios em planos de contingência (competência da DGDEC)', 'Não executar ações de resposta', 'Não articular com municípios sobre adesão (competência das REDECs via DREDEC)'], ARRAY['Conteúdos técnicos fornecidos pela DGDEC, CESTAD e CEMADEN-RJ', 'Demanda dimensionada pela SUOP', 'Apoio logístico das REDECs para capacitação no território', 'Conteúdo doutrinário produzido na AE 8.2.5 como base didática']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7') AND ordem = 6), (SELECT id FROM setores WHERE codigo = 'ESDEC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7'), 7, 'REDECs (via DREDEC) — EXECUTORAS NA PONTA',
    'REDECs (via DREDEC)', 'EXECUTORAS NA PONTA', 'As REDECs são o elo entre o Estado e os municípios no território. Nesta ação, atuam nas duas dimensões: na preparação, apoiam a realização de exercícios simulados, a articulação com COMDECs e a identificação de necessidades municipais; durante a resposta, operacionalizam o suporte estadual no território, servindo como elo entre os órgãos estaduais e as estruturas municipais de resposta. Podem atuar em conjunto com vários órgãos (DGDEC, CESTAD, ESDEC), integrando as frentes no território.',
    ARRAY['Articular com as COMDECs a adesão ao regime de colaboração e identificar necessidades municipais em preparação e resposta', 'Apoiar a realização de exercícios simulados nos municípios de sua região', 'Operacionalizar o suporte estadual no território durante situações de resposta', 'Coletar e reportar necessidades, dificuldades e lições aprendidas'], ARRAY['Não definir protocolos de resposta (competência do CESTAD e da DGDEC)', 'Não capacitar formalmente agentes municipais (competência da ESDEC)', 'Não formalizar convênios sem aprovação da SUBSEGAD e validação da ASSEJUR'], ARRAY['Orientações da DGDEC e do CESTAD para atuação em preparação e resposta', 'Priorização de municípios definida pela SUOP', 'Aprovação da SUBSEGAD para formalização de convênios e validação da ASSEJUR']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7') AND ordem = 7), (SELECT id FROM setores WHERE codigo = 'REDECS'), 'principal');
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7') AND ordem = 7), (SELECT id FROM setores WHERE codigo = 'DREDEC'), 'coordenador');
INSERT INTO fundamentacoes (acao_estrategica_id, introducao, conclusao)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7'), 'A colaboração do Estado com os municípios em preparação e resposta enfrenta três obstáculos:', 'Esta ação enfrenta esses obstáculos instituindo um regime de colaboração em duas dimensões coordenado pela SUOP: preparação (assessoria ao planejamento de contingência, capacitação e exercícios simulados) e resposta (mobilização coordenada de suporte operacional quando a capacidade municipal for excedida), com a DGDEC como eixo operacional, o CESTAD na coordenação de crises, o CEMADEN-RJ nos alertas, o GRAC na integração multisetorial, a ESDEC na capacitação e as REDECs na execução territorial.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7')), 1, '1. Preparação insuficiente: A maioria dos municípios fluminenses não dispõe de planos de contingência atualizados, não realiza exercícios simulados regulares e não tem agentes capacitados para coordenar a resposta a desastres. Quando o evento ocorre, a resposta local é improvisada e a demanda por suporte estadual chega sem planejamento prévio.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7')), 2, '2. Resposta desarticulada: A mobilização do suporte estadual em situação de crise envolve múltiplos órgãos (DGDEC, CESTAD, CEMADEN-RJ, GRAC, REDECs). Sem coordenação prévia e protocolos definidos, a atuação é fragmentada: cada órgão atua no seu tempo, com seus fluxos, gerando sobreposição de esforços em alguns pontos e lacunas em outros.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.2.7')), 3, '3. Dependência crônica do Estado: Municípios que não investem em preparação tornam-se cronicamente dependentes do suporte estadual durante desastres. Isso sobrecarrega os recursos do Estado e reduz a capacidade de atendimento simultâneo a múltiplos municípios afetados.');

-- AE 8.3.1
INSERT INTO acoes_estrategicas (numero, nome, eixo_prioritario_id, objetivo_estrategico_id, estrategia_id,
    acoes_conectadas, descricao_oficial_completa, descricao_o_que, descricao_para_que, ancoragem,
    nota_arranjo_institucional, versao, data_elaboracao, elaboracao, proxima_fase)
VALUES ('8.3.1', 'Articular aparato estatal para apoiar projetos de recuperação quando solicitado pelos municípios', (SELECT id FROM eixos_prioritarios WHERE codigo = 'EIXO_3'), (SELECT id FROM objetivos_estrategicos WHERE codigo = 'OE8'), (SELECT id FROM estrategias WHERE codigo = 'E8.3'),
    'AE 8.3.2 (Conscientização para melhor reconstruir) | AE 8.3.3 (Captação de recursos federais para recuperação) | AE 8.3.4 (Colaboração com municípios na recuperação) | AE 8.2.7 (Colaboração em preparação/resposta)',
    'Estabelecimento de fluxos transversais de comunicação intersetorial e definição de critérios técnicos de priorização (O QUÊ), visando assegurar uma atuação integrada do Estado, direcionada às infraestruturas críticas e fundamentada em avaliações de danos auditáveis (PARA QUÊ).',
    'Estabelecimento de fluxos transversais de comunicação intersetorial e definição de critérios técnicos de priorização.',
    'Assegurar uma atuação integrada do Estado, direcionada às infraestruturas críticas e fundamentada em avaliações de danos auditáveis.',
    'A recuperação pós-desastre exige capacidades que estão fora da Defesa Civil: obras de infraestrutura, habitação, saneamento, energia, rodovias, equipamentos públicos. A SEDEC não executa recuperação — articula o aparato estatal para que os órgãos competentes o façam, de forma integrada e priorizada. O Plano Estratégico 2024–2035 posiciona esta ação na Estratégia E8.3, com foco no estabelecimento de fluxos transversais de comunicação intersetorial e na definição de critérios técnicos de priorização, direcionados às infraestruturas críticas e fundamentados em avaliações de danos auditáveis. A iniciativa indicada é normatizar o procedimento para recebimento dessa demanda.',
    NULL,
    '1.0',
    '2026-02-17',
    'ICTDEC/DAEAD',
    'Fase 2 — Desdobramento Orientado (definição de entregas por setor)');
INSERT INTO destaques_estrategicos (acao_estrategica_id, titulo, header_contexto)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1'), 'Articular, Não Executar', 'CONTEXTO — Recuperação como Ação Transversal do Estado');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1')), 1, 'label_conteudo', 'Princípio', 'A Defesa Civil não reconstrói pontes, não reabilita rodovias, não restaura redes de saneamento. Essas competências pertencem a órgãos setoriais do Estado (infraestrutura, habitação, saneamento, energia, rodovias). O papel da SEDEC é articular: receber a demanda municipal, avaliar danos tecnicamente, priorizar com critérios auditáveis e acionar o aparato estatal de forma integrada, por meio do GRAC. A qualidade da recuperação depende da qualidade dessa articulação.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1')), 2, 'label_conteudo', 'O fluxo da ação', 'Recebimento da demanda: o município solicita apoio à SEDEC. As REDECs e a DGDEC são o canal de entrada.
Avaliação de danos: as REDECs fazem o levantamento na ponta; a Coordenadoria de Engenharia avalia a infraestrutura; a DGDEC consolida a avaliação.
Priorização: a SUOP define os critérios de priorização técnica (infraestruturas críticas, população afetada, risco residual) e ordena as demandas.
Articulação intersetorial: o GRAC mobiliza os órgãos estaduais competentes (infraestrutura, habitação, saneamento e demais) para execução dos projetos de recuperação priorizados.
Acompanhamento: a SUOP monitora o andamento das ações de recuperação articuladas.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1')), 3, 'label_conteudo', 'Risco a evitar', 'Ausência de fluxo normatizado. Sem procedimento claro de recebimento, avaliação e encaminhamento, as demandas municipais de recuperação chegam de forma desestruturada, a priorização é política (não técnica) e os órgãos setoriais não são acionados de forma integrada.');
