VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1'), 1, 'SUOP', 'Líder da Ação', 'Define critérios de priorização; coordena o fluxo de demandas; monitora execução', 'Não executa recuperação; não articula diretamente com órgãos externos');
