INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1'), 1, 'SUOP (sob aval da SUBSEGOP) — LÍDER DA AÇÃO',
    'SUOP (sob aval da SUBSEGOP)', 'LÍDER DA AÇÃO', 'A recuperação pós-desastre envolve órgãos estaduais que não estão subordinados à SEDEC. A articulação do aparato estatal exige coordenação centralizada dentro da Secretaria para que as demandas municipais sejam recebidas, avaliadas tecnicamente, priorizadas e encaminhadas de forma integrada. A SUOP lidera esse fluxo internamente, definindo critérios de priorização e monitorando o andamento das ações articuladas.',
    ARRAY['Normatizar o procedimento de recebimento, avaliação e encaminhamento de demandas municipais de recuperação', 'Definir critérios técnicos de priorização (infraestruturas críticas, população afetada, risco residual)', 'Coordenar o fluxo interno de demandas entre os órgãos da SEDEC (DGDEC, REDECs, Coord. Engenharia)', 'Monitorar o andamento das ações de recuperação articuladas por meio do GRAC'], ARRAY['Não executar projetos de recuperação (competência dos órgãos setoriais do Estado)', 'Não articular diretamente com órgãos estaduais externos (competência do GRAC)', 'Não avaliar danos na ponta (competência das REDECs e da Coord. Engenharia)'], ARRAY['Avaliações de danos consolidadas pela DGDEC e pela Coord. Engenharia', 'Colaboração do GRAC na mobilização dos órgãos setoriais', 'REDECs como canal de levantamento territorial']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'SUOP'), 'principal');
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'SUBSEGOP'), 'aval');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1'), 2, 'GRAC (Grupo de Ações Coordenadas) — ARTICULADOR COM ÓRGÃOS SETORIAIS',
    'GRAC (Grupo de Ações Coordenadas)', 'ARTICULADOR COM ÓRGÃOS SETORIAIS', 'O GRAC é a instância de integração dos órgãos participantes do SIEPDEC-RJ. Nesta ação, é o executor no nível articulatório: recebe da SUOP as demandas priorizadas e mobiliza os órgãos estaduais competentes (infraestrutura, habitação, saneamento, energia, rodovias e demais) para a execução dos projetos de recuperação. O GRAC não executa a recuperação — articula quem a executa.',
    ARRAY['Mobilizar órgãos estaduais competentes para execução dos projetos de recuperação priorizados pela SUOP', 'Estabelecer e manter fluxos transversais de comunicação intersetorial para as ações de recuperação', 'Acompanhar junto aos órgãos setoriais o andamento dos projetos articulados', 'Reportar à SUOP o status das ações e eventuais obstáculos na mobilização intersetorial'], ARRAY['Não avaliar danos (competência da DGDEC, das REDECs e da Coord. Engenharia)', 'Não priorizar demandas (competência da SUOP)', 'Não executar projetos de recuperação (competência dos órgãos setoriais do Estado)'], ARRAY['Demandas priorizadas pela SUOP com critérios técnicos auditáveis', 'Avaliações de danos que fundamentem a solicitação aos órgãos setoriais', 'Receptividade dos órgãos estaduais para atuação articulada']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1') AND ordem = 2), (SELECT id FROM setores WHERE codigo = 'GRAC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1'), 3, 'DGDEC — CANAL COM MUNICÍPIOS E CONSOLIDAÇÃO DE DANOS',
    'DGDEC', 'CANAL COM MUNICÍPIOS E CONSOLIDAÇÃO DE DANOS', 'A DGDEC é o canal formal da relação estado-município nas ações de resposta e recuperação. Nesta ação, recebe as demandas municipais de apoio à recuperação, consolida as avaliações de danos realizadas pelas REDECs e pela Coordenadoria de Engenharia e apresenta à SUOP o panorama consolidado que fundamentará a priorização.',
    ARRAY['Receber e registrar as demandas municipais de apoio à recuperação', 'Consolidar as avaliações de danos realizadas pelas REDECs e pela Coordenadoria de Engenharia', 'Apresentar à SUOP o panorama consolidado de danos e necessidades, fundamentando a priorização', 'Manter o registro auditável das demandas recebidas e das avaliações realizadas'], ARRAY['Não priorizar demandas (competência da SUOP)', 'Não articular com órgãos estaduais externos (competência do GRAC)', 'Não avaliar tecnicamente infraestrutura (competência da Coord. Engenharia)'], ARRAY['Levantamentos das REDECs como base da consolidação', 'Pareceres da Coordenadoria de Engenharia sobre infraestrutura', 'Procedimento normatizado pela SUOP para recebimento e encaminhamento']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1') AND ordem = 3), (SELECT id FROM setores WHERE codigo = 'DGDEC'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1'), 4, 'Coordenadoria de Engenharia em Defesa Civil — AVALIAÇÃO TÉCNICA DE INFRAESTRUTURA',
    'Coordenadoria de Engenharia em Defesa Civil', 'AVALIAÇÃO TÉCNICA DE INFRAESTRUTURA', 'A Coordenadoria de Engenharia avalia danos em infraestrutura e emite pareceres técnicos que fundamentam a priorização e o encaminhamento das demandas de recuperação. Nesta ação, sua contribuição é técnica: avaliar o estado de infraestruturas críticas (pontes, vias, encostas, edificações públicas) e emitir pareceres que subsidiem a SUOP na priorização e o GRAC na solicitação aos órgãos setoriais. A carência de quadro técnico exige priorização rigorosa das avaliações.',
    ARRAY['Avaliar danos em infraestruturas críticas nos municípios afetados, quando demandado', 'Emitir pareceres técnicos sobre o estado da infraestrutura e a necessidade de intervenção', 'Subsidiar a SUOP com informações técnicas para a priorização das demandas de recuperação', 'Articular com órgãos estaduais de infraestrutura quando necessário para complementar avaliações'], ARRAY['Não executar obras de recuperação (competência dos órgãos setoriais do Estado)', 'Não consolidar avaliações gerais de danos (competência da DGDEC)', 'Não priorizar demandas (competência da SUOP)'], ARRAY['Demandas priorizadas pela SUOP dado o quadro limitado de pessoal', 'Levantamentos das REDECs como informação preliminar', 'Acesso ao território afetado em condições adequadas de segurança']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1') AND ordem = 4), (SELECT id FROM setores WHERE codigo = 'COORD_ENGENHARIA'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1'), 5, 'REDECs (via DREDEC) — LEVANTAMENTO NA PONTA',
    'REDECs (via DREDEC)', 'LEVANTAMENTO NA PONTA', 'As REDECs são o primeiro elo do levantamento de danos no território. Nesta ação, realizam o levantamento preliminar de danos nos municípios afetados, coletam as demandas de recuperação junto às COMDECs e reportam à DGDEC. Suas informações alimentam tanto a consolidação da DGDEC quanto as avaliações da Coordenadoria de Engenharia.',
    ARRAY['Realizar levantamento preliminar de danos nos municípios afetados de sua região', 'Coletar e reportar à DGDEC as demandas municipais de apoio à recuperação', 'Apoiar a Coordenadoria de Engenharia no acesso ao território para avaliações técnicas', 'Acompanhar e reportar o andamento das ações de recuperação nos municípios de sua região'], ARRAY['Não avaliar tecnicamente infraestrutura (competência da Coord. Engenharia)', 'Não priorizar demandas (competência da SUOP)', 'Não articular com órgãos estaduais externos (competência do GRAC)'], ARRAY['Procedimento normatizado pela SUOP para coleta e reporte', 'Acesso ao território afetado em condições adequadas de segurança', 'Coordenação da DGDEC sobre prioridades e cronograma de levantamento']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1') AND ordem = 5), (SELECT id FROM setores WHERE codigo = 'REDECS'), 'principal');
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1') AND ordem = 5), (SELECT id FROM setores WHERE codigo = 'DREDEC'), 'coordenador');
INSERT INTO fundamentacoes (acao_estrategica_id, introducao, conclusao)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1'), 'A articulação do aparato estatal para recuperação pós-desastre enfrenta três obstáculos:', 'Esta ação enfrenta esses obstáculos instituindo um fluxo normatizado de recebimento, avaliação, priorização e articulação: as REDECs e a DGDEC recebem e consolidam as demandas; a Coordenadoria de Engenharia avalia a infraestrutura; a SUOP define a priorização com critérios técnicos; e o GRAC mobiliza os órgãos estaduais competentes para a execução.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1')), 1, '1. Ausência de fluxo normatizado: Não há procedimento padronizado para que os municípios solicitem apoio à recuperação e para que a SEDEC receba, avalie e encaminhe essas demandas. As solicitações chegam por canais informais, sem documentação técnica adequada e sem critérios de priorização.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1')), 2, '2. Priorização política em vez de técnica: Sem avaliações de danos auditáveis e critérios técnicos definidos, a priorização das demandas de recuperação tende a ser política. Municípios com maior articulação política recebem atenção antes de municípios com maior necessidade técnica. Isso compromete a efetividade dos recursos do Estado e gera questionamentos dos órgãos de controle.');
INSERT INTO fundamentacao_itens (fundamentacao_id, ordem, conteudo)
VALUES ((SELECT id FROM fundamentacoes WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.3.1')), 3, '3. Desconexão entre SEDEC e órgãos executores: A SEDEC identifica a necessidade de recuperação, mas não tem fluxo estabelecido para acionar os órgãos estaduais que executam as obras e intervenções. Sem o GRAC como instância articuladora, a comunicação com os órgãos setoriais é ad hoc e depende de relações pessoais.');

-- AE 8.3.2
INSERT INTO acoes_estrategicas (numero, nome, eixo_prioritario_id, objetivo_estrategico_id, estrategia_id,
    acoes_conectadas, descricao_oficial_completa, descricao_o_que, descricao_para_que, ancoragem,
    nota_arranjo_institucional, versao, data_elaboracao, elaboracao, proxima_fase)
VALUES ('8.3.2', 'Promover conscientização dos órgãos estaduais relacionados às ações de recuperação quanto aos princípios para melhor reconstruir após desastres', (SELECT id FROM eixos_prioritarios WHERE codigo = 'EIXO_3'), (SELECT id FROM objetivos_estrategicos WHERE codigo = 'OE8'), (SELECT id FROM estrategias WHERE codigo = 'E8.3'),
    'AE 8.3.1 (Articulação do aparato estatal) | AE 8.3.4 (Colaboração com municípios na recuperação) | AE 9.2.1 (Políticas transversais de RRD)',
    'Construção e difusão de diretrizes de redução de riscos junto aos órgãos executores de obras e infraestrutura (O QUÊ), visando evitar a reconstrução de cenários de vulnerabilidade e promover a resiliência em investimentos de recuperação (PARA QUÊ).',
    'Construção e difusão de diretrizes de redução de riscos junto aos órgãos executores de obras e infraestrutura.',
    'Evitar a reconstrução de cenários de vulnerabilidade e promover a resiliência em investimentos de recuperação.',
    'A recuperação pós-desastre é uma oportunidade para reduzir vulnerabilidades. O princípio de “reconstruir melhor” (build back better), previsto no Marco de Sendai e incorporado pelo PN-PDC 2025–2035, orienta que as ações de recuperação não reproduzam os cenários de risco que existiam antes do desastre. No entanto, os órgãos estaduais que executam obras e intervenções de infraestrutura não necessariamente incorporam esses princípios em seus projetos. O Plano Estratégico 2024–2035 posiciona esta ação na Estratégia E8.3, com foco na construção e difusão de diretrizes de redução de riscos junto aos órgãos executores. As iniciativas indicadas são o desenvolvimento de atividades de nivelamento e a produção de mídias para difusão.',
    NULL,
    '1.0',
    '2026-02-17',
    'ICTDEC/DAEAD',
    'Fase 2 — Desdobramento Orientado (definição de entregas por setor)');
INSERT INTO destaques_estrategicos (acao_estrategica_id, titulo, header_contexto)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.3.2'), 'Influenciar Quem Reconstrói', 'CONTEXTO — Recuperação como Oportunidade de Redução de Risco');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.3.2')), 1, 'label_conteudo', 'Princípio', 'A SEDEC não executa obras de recuperação. Quem executa são órgãos estaduais de infraestrutura, habitação, saneamento e outros. Esses órgãos, contudo, nem sempre incorporam princípios de redução de riscos em seus projetos de reconstrução. O resultado é que a recuperação reproduz as mesmas vulnerabilidades que o desastre expôs. A SEDEC pode mudar esse cenário por meio de influência técnica: produzindo diretrizes fundamentadas, nivelando os órgãos executores e difundindo os princípios de reconstrução resiliente. A efetividade dessa influência depende de articulação político-institucional no nível adequado.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.3.2')), 2, 'label_conteudo', 'Duas frentes', 'Produção de diretrizes: o ICTDEC (com CEPEDEC) produz o conteúdo técnico-científico — diretrizes de reconstrução resiliente, fundamentadas nos princípios do Marco de Sendai e do PN-PDC. A Coordenadoria de Engenharia contribui com o conteúdo técnico de engenharia aplicável.
Difusão e articulação: a SUBSEGOP articula politicamente o acesso aos órgãos estaduais executores, conferindo legitimidade institucional à difusão. A SUOP valida o alinhamento estratégico e coordena a execução interna.');
INSERT INTO destaque_linhas (destaque_id, ordem, tipo, label, conteudo)
VALUES ((SELECT id FROM destaques_estrategicos WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.3.2')), 3, 'label_conteudo', 'Risco a evitar', 'Produzir diretrizes sem capacidade de difusão. Sem articulação político-institucional, as diretrizes da SEDEC não chegam aos órgãos que executam a recuperação. A qualidade do conteúdo técnico é condição necessária, mas não suficiente: é preciso que o interlocutor institucional tenha peso para ser ouvido.');
INSERT INTO panoramico_linhas (acao_estrategica_id, ordem, setor_display, papel, sintese_contribuicao, nao_faz)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.3.2'), 1, 'ICTDEC (com CEPEDEC)', 'Líder da Produção de Diretrizes', 'Produz diretrizes de reconstrução resiliente; estrutura conteúdo para difusão', 'Não articula politicamente com órgãos externos');
