INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.3.2'), 2, 'Coordenadoria de Engenharia em Defesa Civil — CONTEÚDO TÉCNICO DE ENGENHARIA',
    'Coordenadoria de Engenharia em Defesa Civil', 'CONTEÚDO TÉCNICO DE ENGENHARIA', 'A Coordenadoria de Engenharia detém o conhecimento técnico sobre intervenções físicas em defesa civil. Nesta ação, fornece ao ICTDEC o conteúdo técnico de engenharia resiliente aplicável à recuperação: especificações técnicas para reconstrução de infraestrutura com redução de risco, critérios de resiliência para obras e exemplos de boas práticas. Esse conteúdo será integrado às diretrizes produzidas pelo ICTDEC.',
    ARRAY['Fornecer ao ICTDEC conteúdo técnico de engenharia resiliente aplicável à recuperação pós-desastre', 'Contribuir com especificações técnicas, critérios de resiliência e exemplos de boas práticas', 'Validar tecnicamente o conteúdo de engenharia nas diretrizes antes da difusão'], ARRAY['Não produzir diretrizes conceituais de reconstrução resiliente (competência do ICTDEC/CEPEDEC)', 'Não difundir conteúdo junto aos órgãos estaduais', 'Não executar obras de recuperação'], ARRAY['Demandas do ICTDEC sobre conteúdos específicos necessários', 'Acesso a referências técnicas nacionais e internacionais sobre engenharia resiliente']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '8.3.2') AND ordem = 2), (SELECT id FROM setores WHERE codigo = 'COORD_ENGENHARIA'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '8.3.2'), 3, 'SUOP (sob aval da SUBSEGOP) — VALIDAÇÃO E COORDENAÇÃO',
    'SUOP (sob aval da SUBSEGOP)', 'VALIDAÇÃO E COORDENAÇÃO', 'A SUOP comanda os órgãos setoriais e é responsável pelo alinhamento estratégico das ações operacionais. Nesta ação, valida os produtos quanto ao alinhamento ao Plano Estratégico e coordena a execução interna, assegurando que o conteúdo produzido pelo ICTDEC e pela Coordenadoria de Engenharia esteja pronto para difusão.',
    ARRAY['Validar as diretrizes quanto ao alinhamento ao Plano Estratégico e às prioridades da SEDEC', 'Coordenar a execução interna da ação (cronograma, integração entre ICTDEC e Coord. Engenharia)', 'Articular com a SUBSEGOP o encaminhamento para difusão junto aos órgãos estaduais'], ARRAY['Não produzir conteúdo técnico (competência do ICTDEC e da Coord. Engenharia)', 'Não articular politicamente com órgãos estaduais externos (competência da SUBSEGOP)', 'Não executar atividades de nivelamento'], ARRAY['Produtos apresentados pelo ICTDEC com tempo hábil para revisão', 'Disponibilidade da SUBSEGOP para conduzir a articulação política']);
