INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '9.2.1'), 1, 'SUBSEGOP — LÍDER DA ARTICULAÇÃO POLÍTICO-INSTITUCIONAL',
    'SUBSEGOP', 'LÍDER DA ARTICULAÇÃO POLÍTICO-INSTITUCIONAL', 'A SUBSEGOP é a instância que faz o elo entre os temas operacionais e políticos da Secretaria. Nesta ação, lidera a articulação político-institucional com órgãos estaduais, atores externos e parceiros estratégicos para o estabelecimento de instâncias de discussão sobre políticas transversais de RRD. A interlocução com órgãos de outros setores de governo exige peso institucional que somente a SUBSEGOP pode conferir. Atualmente, essa função não conta com órgão de assessoramento dedicado (ver Nota sobre Arranjo Institucional).',
    ARRAY['Conduzir o diálogo institucional com órgãos estaduais de outros setores (ordenamento territorial, saúde, educação, meio ambiente, infraestrutura, habitação)', 'Viabilizar a criação e o funcionamento de instâncias de discussão intersetorial sobre políticas de RRD', 'Articular a participação de atores externos e parceiros estratégicos nas discussões', 'Representar a SEDEC nos fóruns intersetoriais de políticas públicas'], ARRAY['Não produzir conteúdo técnico (competência do ICTDEC/CEPEDEC)', 'Não conduzir capacitações (competência da ESDEC)', 'Não coordenar internamente os órgãos da SEDEC para esta ação (competência da SUOP)'], ARRAY['Conteúdo técnico produzido pelo ICTDEC para fundamentar as discussões', 'Coordenação interna da SUOP para alinhar a participação dos órgãos da SEDEC', 'Receptividade dos órgãos estaduais para o diálogo intersetorial']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
VALUES ((SELECT id FROM fichas WHERE acao_estrategica_id = (SELECT id FROM acoes_estrategicas WHERE numero = '9.2.1') AND ordem = 1), (SELECT id FROM setores WHERE codigo = 'SUBSEGOP'), 'principal');
INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '9.2.1'), 2, 'ICTDEC (com CEPEDEC) — CONTEÚDO TÉCNICO E METODOLÓGICO',
    'ICTDEC (com CEPEDEC)', 'CONTEÚDO TÉCNICO E METODOLÓGICO', 'O ICTDEC, por meio do CEPEDEC, é o produtor de conhecimento científico e metodologias da Secretaria. Nesta ação, fornece o subsídio técnico que fundamenta e qualifica as discussões intersetoriais: estudos sobre transversalidade da RRD, metodologias para integração de políticas públicas, análises de impacto e propostas de diretrizes. O ICTDEC não conduz a articulação política (competência da SUBSEGOP), mas produz o conteúdo que lhe dá substância.',
    ARRAY['Produzir estudos e metodologias sobre transversalidade da RRD nas políticas públicas estaduais', 'Elaborar subsídios técnicos para as discussões intersetoriais (notas técnicas, análises, propostas de diretrizes)', 'Apoiar a ESDEC com conteúdo para capacitação em produção de políticas transversais de RRD', 'Sistematizar os resultados das discussões intersetoriais em documentos técnicos'], ARRAY['Não articular politicamente com órgãos estaduais externos (competência da SUBSEGOP)', 'Não conduzir capacitações (competência da ESDEC)', 'Não representar a SEDEC em fóruns políticos'], ARRAY['Demandas da SUBSEGOP e da SUOP sobre temas prioritários', 'Acesso a dados e informações dos órgãos setoriais para produção de estudos', 'Parcerias com IES e ICTs (AE 6.1.3) como fonte complementar de conhecimento']);
INSERT INTO ficha_setores (ficha_id, setor_id, tipo_participacao)
