INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '9.2.1'), 5, 'DGAC — DIMENSÃO COMUNITÁRIA',
    'DGAC', 'DIMENSÃO COMUNITÁRIA', 'A DGAC desenvolve percepção de risco e participação social. Nesta ação, contribui com a dimensão comunitária nas discussões intersetoriais: assegura que as políticas transversais de RRD incorporem a perspectiva das comunidades, a percepção de risco da população e os mecanismos de participação social.',
    ARRAY['Contribuir com conteúdo sobre percepção de risco e participação social nas discussões intersetoriais', 'Assegurar que as políticas transversais incorporem a perspectiva comunitária', 'Participar das instâncias de discussão representando a dimensão de conscientização e participação social'], ARRAY['Não articular com órgãos estaduais fora do tema comunitário', 'Não produzir diretrizes de política pública', 'Não conduzir capacitações formais (competência da ESDEC)'], ARRAY['Coordenação da SUOP sobre participação nas discussões', 'Conteúdo técnico do ICTDEC como referência para contribuições']);
