VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '9.2.1'), 6, 'CEAMA (Coordenadoria Estadual de Ações de Meio Ambiente) — DIMENSÃO AMBIENTAL',
    'CEAMA (Coordenadoria Estadual de Ações de Meio Ambiente)', 'DIMENSÃO AMBIENTAL', 'A CEAMA coordena ações de meio ambiente com impacto em defesa civil. Nesta ação, contribui com a dimensão ambiental nas discussões intersetoriais: integração de políticas ambientais e de RRD, identificação de ações ambientais com impacto na redução de riscos e articulação com órgãos ambientais estaduais.',
    ARRAY['Contribuir com conteúdo sobre integração de políticas ambientais e de RRD nas discussões intersetoriais', 'Identificar pontos de convergência entre agenda ambiental e agenda de redução de riscos', 'Participar das instâncias de discussão representando a dimensão ambiental'], ARRAY['Não articular com órgãos estaduais fora do tema ambiental', 'Não produzir diretrizes gerais de política de RRD', 'Não conduzir capacitações (competência da ESDEC)'], ARRAY['Coordenação da SUOP sobre participação nas discussões', 'Conteúdo técnico do ICTDEC como referência']);
