INSERT INTO fichas (acao_estrategica_id, ordem, titulo, setor_display, papel, justificativa,
    contribuicao_esperada, nao_escopo, dependencias_criticas)
VALUES ((SELECT id FROM acoes_estrategicas WHERE numero = '9.2.1'), 7, 'REDECs (via DREDEC) — PARTICIPAÇÃO MUNICIPAL',
    'REDECs (via DREDEC)', 'PARTICIPAÇÃO MUNICIPAL', 'As REDECs são o elo entre o Estado e os municípios. Nesta ação, articulam a participação dos municípios nas discussões sobre políticas transversais de RRD, assegurando que as propostas reflitam as realidades locais. Coletam demandas e percepções municipais e reportam à SUOP, enriquecendo o debate com a perspectiva territorial.',
    ARRAY['Articular a participação de municípios nas instâncias de discussão sobre políticas transversais de RRD', 'Coletar demandas e percepções municipais sobre a transversalidade da RRD', 'Reportar à SUOP as contribuições e necessidades municipais identificadas'], ARRAY['Não produzir conteúdo técnico (competência do ICTDEC)', 'Não articular com órgãos estaduais (competência da SUBSEGOP)', 'Não produzir diretrizes de política pública'], ARRAY['Coordenação da SUOP sobre participação nas discussões', 'Priorização de municípios para participação']);
